import React, { useState } from 'react';

function Underwater() {
  // 状态管理
  const [selectedDate, setSelectedDate] = useState('');
  const [lineChart, setLineChart] = useState(null);
  const [pieChart, setPieChart] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // 日期范围配置
  const minDate = '2020-05-08';
  const maxDate = '2021-04-05';

  // 路径生成函数
  const generateFilePath = (dateString) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);  // 开始加载
    try {
      const date = new Date(dateString);
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const day = String(date.getDate()).padStart(2, '0');

      return `config/软件工程大作业数据/水质数据/${year}-${month}/${year}-${month}-${day}.json`;
    } catch (error) {
      throw new Error('无效的日期格式');
    }finally {
      setIsLoading(false); // 无论成功失败都结束加载
    }
  };

  // 提交处理
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // 验证日期是否在范围内
    if (!selectedDate || selectedDate < minDate || selectedDate > maxDate) {
      setError('请选择2020-05-08至2021-04-05之间的日期');
      return;
    }

    try {
      // 生成文件路径
      const filePath = generateFilePath(selectedDate);

      // 发送请求
      const response = await fetch('http://localhost:3001/visualize-water', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ file_path: filePath })
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || '服务器错误');
      }

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setLineChart(data.line_chart);
      setPieChart(data.pie_chart);
    } catch (err) {
      setError(err.message);
      setLineChart(null);
      setPieChart(null);
    }
  };

  return (
    <div className="underwater-container">
      <h1>水下数据可视化系统</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          min={minDate}
          max={maxDate}
          required
          style={{ 
            width: '200px',
            padding: '8px',
            marginRight: '10px'
          }}
        />
        <button 
          type="submit"
          disabled={isLoading}  // 添加禁用属性
          style={{
            padding: '8px 16px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          生成报告
        </button>
      </form>

      {error && (
        <div style={{ 
          color: 'red', 
          margin: '10px 0',
          padding: '10px',
          border: '1px solid #ff0000'
        }}>
          {error}
        </div>
      )}

      {/* 加载动画容器 */}
      {isLoading && (
        <div style={styles.loadingOverlay}>
          <div style={styles.loadingSpinner}></div>
          <div style={styles.loadingText}>数据加载中...</div>
        </div>
      )}

      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        gap: '20px',
        marginTop: '20px'
      }}>
        {lineChart && (
          <div style={{
            flex: '1 1 600px',
            padding: '15px',
            border: '1px solid #ddd',
            borderRadius: '8px'
          }}>
            <h2 style={{ color: '#333' }}>水质参数趋势图</h2>
            <img 
              src={`data:image/png;base64,${lineChart}`} 
              alt="水质趋势图"
              style={{ width: '100%', height: 'auto' }}
            />
          </div>
        )}

        {pieChart && (
          <div style={{
            flex: '1 1 400px',
            padding: '15px',
            border: '1px solid #ddd',
            borderRadius: '8px'
          }}>
            <h2 style={{ color: '#333' }}>水质类别分布</h2>
            <img 
              src={`data:image/png;base64,${pieChart}`} 
              alt="水质分布图"
              style={{ width: '100%', height: 'auto' }}
            />
          </div>
        )}
      </div>
    </div>
  );
}

// 动画样式对象
const styles = {
  loadingOverlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(255, 255, 255, 0.9)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
  },
  loadingSpinner: {
    width: '50px',
    height: '50px',
    borderRadius: '50%',
    border: '5px solid #f3f3f3',
    borderTop: '5px solid #3498db',
    animation: 'spin 1s linear infinite',
  },
  loadingText: {
    marginTop: '20px',
    fontSize: '1.2rem',
    color: '#333',
  },
  // 添加 CSS 动画关键帧
  '@keyframes spin': {
    '0%': { transform: 'rotate(0deg)' },
    '100%': { transform: 'rotate(360deg)' }
  }
};

// 动态添加 CSS 关键帧
const styleSheet = document.styleSheets[0];
styleSheet.insertRule(`
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`, styleSheet.cssRules.length);

export default Underwater;
