// src/pages/VideoPage.jsx
import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


const videos = [
  {
    id: 1,
    title: "这是一个海洋鱼类视频",
    description: "这是视频的简介内容，包含了关于这个视频的详细描述和相关信息。",
    cover: "https://picsum.photos/1280/720?random=2",
    duration: "00:20",
    source: "/api/video",
    channel: {
      name: "UP主名称",
      avatar: "https://picsum.photos/32/32?random=1"
    },
    views: "10.2万",
    comments: "500"
  },
  // 其他视频数据...
];

const VideoPage = () => {
  const [currentVideo, setCurrentVideo] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [progress, setProgress] = useState(0);
  const [isMuted, setIsMuted] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const videoRef = useRef(null);
  const controlsRef = useRef(null);
  const navRef = useRef(null);

  // 视频控制逻辑
  const togglePlayPause = () => {
    if (videoRef.current.paused) {
      videoRef.current.play();
    } else {
      videoRef.current.pause();
    }
    setIsPlaying(!videoRef.current.paused);
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    videoRef.current.volume = newVolume;
    setIsMuted(newVolume === 0);
  };

  const updateProgress = () => {
    const progress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
    setProgress(progress);
  };

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      videoRef.current.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        navRef.current.classList.add('py-2');
        navRef.current.classList.remove('py-3');
      } else {
        navRef.current.classList.add('py-3');
        navRef.current.classList.remove('py-2');
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="bg-gray-100 font-sans text-dark min-h-screen">
      <Navbar ref={navRef} />
      
      <main className="container mx-auto px-4 pt-24 pb-12">
        {!currentVideo ? (
          <HomePage videos={videos} onVideoSelect={setCurrentVideo} />
        ) : (
          <VideoPlayer 
            video={currentVideo}
            isPlaying={isPlaying}
            volume={volume}
            progress={progress}
            isMuted={isMuted}
            isFullscreen={isFullscreen}
            onPlayPause={togglePlayPause}
            onVolumeChange={handleVolumeChange}
            onFullscreen={toggleFullscreen}
            videoRef={videoRef}
          />
        )}
      </main>

      <Footer />
    </div>
  );
};

// 导航栏组件
const Navbar = React.forwardRef((props, ref) => (
  <nav ref={ref} className="fixed top-0 left-0 right-0 bg-white shadow-md z-50 transition-all duration-300">
    <div className="container mx-auto px-4 py-3 flex items-center justify-between">
      <div className="flex items-center space-x-4">
        <div className="text-primary text-2xl font-bold">视频平台</div>
        <div className="hidden md:flex space-x-6">
          <a href="#" className="font-medium hover:text-primary transition-colors">首页</a>
          <a href="#" className="font-medium hover:text-primary transition-colors">分类</a>
          <a href="#" className="font-medium hover:text-primary transition-colors">排行榜</a>
          <a href="#" className="font-medium hover:text-primary transition-colors">收藏</a>
        </div>
      </div>

      <div className="flex items-center space-x-4">
        <div className="relative hidden md:block">
          <input 
            type="text" 
            placeholder="搜索视频..." 
            className="pl-10 pr-4 py-2 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-primary/50 w-64 transition-all"
          />
          <FontAwesomeIcon 
             
            className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"
          />
        </div>
        <button className="bg-primary hover:bg-primary/90 text-white py-2 px-4 rounded-full transition-all transform hover:scale-105 flex items-center">
          <FontAwesomeIcon  className="mr-2" />
          <span>投稿</span>
        </button>
        <div className="relative group">
          <img 
            src="https://picsum.photos/32/32" 
            alt="用户头像" 
            className="w-8 h-8 rounded-full cursor-pointer"
          />
          <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-2 z-50 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-300 transform group-hover:translate-y-0 translate-y-2">
            <a href="#" className="block px-4 py-2 hover:bg-gray-100 transition-colors">
              <FontAwesomeIcon  className="mr-2" />个人中心
            </a>
            <a href="#" className="block px-4 py-2 hover:bg-gray-100 transition-colors">
              <FontAwesomeIcon  className="mr-2" />设置
            </a>
            <a href="#" className="block px-4 py-2 hover:bg-gray-100 transition-colors">
              <FontAwesomeIcon  className="mr-2" />退出登录
            </a>
          </div>
        </div>
      </div>
    </div>
  </nav>
));

// 首页组件
const HomePage = ({ videos, onVideoSelect }) => (
  <div id="home-page">
    <Carousel />
    <CategoryNav />
    <VideoList videos={videos} onSelect={onVideoSelect} />
  </div>
);

// 轮播图组件
const Carousel = () => (
  <div className="mb-8 rounded-xl overflow-hidden shadow-lg">
    <div className="relative h-64 md:h-80 bg-gray-900">
      <img 
        src="https://picsum.photos/1200/400" 
        alt="轮播图" 
        className="w-full h-full object-cover"
      />
      <div className="absolute inset-0 bg-gradient-to-t from-black/70 to-transparent flex items-end">
        <div className="p-6 text-white">
          <h2 className="text-2xl md:text-3xl font-bold mb-2">热门推荐视频</h2>
          <p className="text-gray-300 max-w-2xl">这是一个精彩的推荐视频，包含了丰富的内容和有趣的情节</p>
        </div>
      </div>
    </div>
  </div>
);

// 分类导航组件
const CategoryNav = () => (
  <div className="mb-8 bg-white rounded-xl shadow-md p-4">
    <div className="flex items-center justify-between mb-4">
      <h2 className="text-xl font-bold">分类导航</h2>
      <a href="#" className="text-primary hover:underline">更多</a>
    </div>
    <div className="flex flex-wrap gap-3">
      {['全部', '动画', '游戏', '音乐', '科技', '生活', '影视', '综艺', '体育'].map((category) => (
        <button 
          key={category}
          className={`px-4 py-2 rounded-full ${
            category === '全部' 
              ? 'bg-primary text-white hover:bg-primary/90' 
              : 'bg-gray-100 hover:bg-gray-200'
          } transition-colors`}
        >
          {category}
        </button>
      ))}
    </div>
  </div>
);

// 视频列表组件
const VideoList = ({ videos, onSelect }) => (
  <div className="mb-8">
    <div className="flex items-center justify-between mb-4">
      <h2 className="text-xl font-bold">推荐视频</h2>
      <div className="flex space-x-2">
        <button className="px-3 py-1 rounded bg-gray-100 hover:bg-gray-200 transition-colors">最新</button>
        <button className="px-3 py-1 rounded bg-gray-100 hover:bg-gray-200 transition-colors">最热</button>
      </div>
    </div>
    
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {videos.map(video => (
        <VideoCard key={video.id} video={video} onSelect={onSelect} />
      ))}
    </div>

    <div className="mt-8 text-center">
      <button className="bg-white border border-gray-300 text-gray-700 py-2 px-6 rounded-lg hover:bg-gray-50 transition-colors">
        加载更多
      </button>
    </div>
  </div>
);

// 视频卡片组件
const VideoCard = ({ video, onSelect }) => (
  <div 
    className="bg-white rounded-xl shadow-md overflow-hidden card-hover cursor-pointer"
    onClick={() => onSelect(video)}
  >
    <div className="relative group">
      <img 
        src={video.cover} 
        alt="视频封面" 
        className="w-full aspect-video object-cover transition-transform duration-500 group-hover:scale-105"
      />
      <div className="absolute inset-0 bg-black/0 group-hover:bg-black/40 transition-colors flex items-center justify-center opacity-0 group-hover:opacity-100">
        <div className="w-12 h-12 rounded-full bg-primary/80 flex items-center justify-center transform scale-90 group-hover:scale-100 transition-transform">
          <FontAwesomeIcon  className="text-white text-xl ml-1" />
        </div>
      </div>
      <div className="absolute bottom-2 right-2 bg-black/60 text-white text-xs py-0.5 px-1.5 rounded">
        {video.duration}
      </div>
    </div>
    <div className="p-4">
      <h3 className="font-medium line-clamp-2 mb-2">{video.title}</h3>
      <div className="flex items-center justify-between">
        <div className="flex items-center">
          <img 
            src={video.channel.avatar} 
            alt="UP主头像" 
            className="w-6 h-6 rounded-full mr-2"
          />
          <span className="text-sm text-gray-600 truncate">{video.channel.name}</span>
        </div>
        <div className="flex items-center text-sm text-gray-600">
          <FontAwesomeIcon  className="mr-1" />
          <span>{video.views}</span>
        </div>
      </div>
    </div>
  </div>
);

// 视频播放器组件
const VideoPlayer = ({ video, videoRef, ...props }) => {
  const [showControls, setShowControls] = useState(true);
  
  // 控制条自动隐藏逻辑
  useEffect(() => {
    let timeout;
    const resetTimer = () => {
      clearTimeout(timeout);
      setShowControls(true);
      timeout = setTimeout(() => setShowControls(false), 3000);
    };

    videoRef.current.addEventListener('mousemove', resetTimer);
    return () => {
      videoRef.current.removeEventListener('mousemove', resetTimer);
      clearTimeout(timeout);
    };
  }, []);

  return (
    <div className="flex flex-col md:flex-row gap-6">
      <div className="w-full md:w-2/3">
        <div className="bg-white rounded-xl shadow-lg overflow-hidden transition-all hover:shadow-xl">
          <video 
            ref={videoRef}
            className="w-full h-full object-contain"
            poster={video.cover}
            onTimeUpdate={props.updateProgress}
          >
            <source src={video.source} type="video/mp4" />
          </video>

          {showControls && (
            <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
              {/* 控制条实现 */}
            </div>
          )}
          
          {/* 视频信息部分 */}
        </div>
      </div>
      
      <div className="w-full md:w-1/3">

      </div>
    </div>
  );
};

// 页脚组件
const Footer = () => (
  <footer className="bg-white border-t border-gray-200 py-8">
    <div className="container mx-auto px-4">
      {/* 页脚内容 */}
    </div>
  </footer>
);

export default VideoPage;
