import { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';
import { motion } from 'framer-motion';

function ChatPage() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  
  const messagesEndRef = useRef(null);
  const messageContainerRef = useRef(null);
  
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);
  
  const handleInputChange = (e) => {
    setInput(e.target.value);
  };
  
  const getStreamingResponse = async (message, messageId) => {
    try {
      const response = await fetch('http://localhost:3001/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message }),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const reader = response.body.getReader();
      const decoder = new TextDecoder('utf-8');
      let buffer = '';
      
      while (true) {
        const { done, value } = await reader.read();
        
        if (done) {
          break;
        }
        
        buffer += decoder.decode(value, { stream: true });
        
        const lines = buffer.split('\n\n');
        buffer = lines.pop() || '';
        
        for (const line of lines) {
          if (line.startsWith('data: ')) {
            try {
              const data = JSON.parse(line.slice(6));
              
              setMessages(prev => 
                prev.map(msg => 
                  msg.id === messageId 
                    ? { 
                        ...msg, 
                        content: msg.content + (data.content || ''),
                        ...(data.finished ? { isStreaming: false } : {})
                      } 
                    : msg
                )
              );
              
              if (data.error) {
                throw new Error(data.error);
              }
            } catch (e) {
              console.error('Error parsing SSE data:', e);
            }
          }
        }
      }
      
      setMessages(prev => 
        prev.map(msg => 
          msg.id === messageId 
            ? { ...msg, isStreaming: false } 
            : msg
        )
      );
      
    } catch (error) {
      console.error('Error in streaming response:', error);
      setMessages(prev => 
        prev.map(msg => 
          msg.id === messageId 
            ? { ...msg, content: msg.content + '\n\n[ËøûÊé•‰∏≠Êñ≠] ' + error.message, isStreaming: false } 
            : msg
        )
      );
    }
  };
  
  const simulateStreamingResponse = async (message, messageId) => {
    try {
      const words = `ËøôÊòØ‰∏Ä‰∏™Ê®°ÊãüÂõûÂ∫î„ÄÇÁõÆÂâçÊó†Ê≥ïËøûÊé•Âà∞ÂêéÁ´ØÊúçÂä°Âô®ÔºåÊâÄ‰ª•ÊòæÁ§∫ËøôÊù°ÊµãËØïÊ∂àÊÅØ„ÄÇÊÇ®ÁöÑÈóÆÈ¢òÊòØÂÖ≥‰∫é: "${message}"„ÄÇËØ∑Á°Æ‰øùÂêéÁ´ØÊúçÂä°Âô®Ê≠£Âú®ËøêË°åÔºåÂπ∂‰∏îÂèØ‰ª•‰ªéÂâçÁ´ØËÆøÈóÆAPIÁ´ØÁÇπ /api/chat„ÄÇ`;
      const wordArray = words.split(' ');
      
      for (let i = 0; i < wordArray.length; i++) {
        setMessages(prev => 
          prev.map(msg => 
            msg.id === messageId 
              ? { 
                  ...msg, 
                  content: msg.content + (i > 0 ? ' ' : '') + wordArray[i],
                  ...(i === wordArray.length - 1 ? { isStreaming: false } : {})
                } 
              : msg
          )
        );
        
        await new Promise(resolve => setTimeout(resolve, 50));
      }
    } catch (error) {
      console.error('Error in simulation:', error);
      setMessages(prev => 
        prev.map(msg => 
          msg.id === messageId 
            ? { ...msg, content: 'Ëé∑ÂèñÂõûÂ∫îÊó∂Âá∫Èîô', isStreaming: false } 
            : msg
        )
      );
    }
  };
  
  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    
