import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import LoginForm from '../components/LoginForm';
import RegisterForm from '../components/RegisterForm';

import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';
import { motion } from 'framer-motion';

// 自定义波浪效果组件
const WaterWave = ({ className }) => {
  return (
    <div className={`absolute bottom-0 w-full overflow-hidden ${className}`}>
      <svg 
        className="relative block w-[calc(100%+1.3px)] h-[150px]"
        data-name="Layer 1" 
        xmlns="http://www.w3.org/2000/svg" 
        viewBox="0 0 1200 120" 
        preserveAspectRatio="none"
      >
        <path 
          d="M0,0V46.29c47.79,22.2,103.59,32.17,158,28,70.36-5.37,136.33-33.31,206.8-37.5C438.64,32.43,512.34,53.67,583,72.05c69.27,18,138.3,24.88,209.4,13.08,36.15-6,69.85-17.84,104.45-29.34C989.49,25,1113-14.29,1200,52.47V0Z" 
          className="fill-current text-blue-500 opacity-30" 
        ></path>
        <path 
          d="M0,0V15.81C13,36.92,27.64,56.86,47.69,72.05,99.41,111.27,165,111,224.58,91.58c31.15-10.15,60.09-26.07,89.67-39.8,40.92-19,84.73-46,130.83-49.67,36.26-2.85,70.9,9.42,98.6,31.56,31.77,25.39,62.32,62,103.63,73,40.44,10.79,81.35-6.69,119.13-24.28s75.16-39,116.92-43.05c59.73-5.85,113.28,22.88,168.9,38.84,30.2,8.66,59,6.17,87.09-7.5,22.43-10.89,48-26.93,60.65-49.24V0Z" 
          className="fill-current text-teal-400 opacity-40" 
        ></path>
        <path 
          d="M0,0V5.63C149.93,59,314.09,71.32,475.83,42.57c43-7.64,84.23-20.12,127.61-26.46,59-8.63,112.48,12.24,165.56,35.4C827.93,77.22,886,95.24,951.2,90c86.53-7,172.46-45.71,248.8-84.81V0Z" 
          className="fill-current text-cyan-300 opacity-30" 
        ></path>
      </svg>
    </div>
  );
};

const AuthPage = () => {
  const [isLoginMode, setIsLoginMode] = useState(true);
  const { isAuthenticated, userInfo } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/user-dashboard');
    }
  }, [isAuthenticated, navigate]);

  // 粒子效果配置
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  const particlesOptions = {
    background: {
      color: {
        value: "transparent",
      },
    },
    fpsLimit: 60,
    particles: {
      color: {
        value: ["#00FFFF", "#00BFFF", "#1E90FF", "#87CEFA"],
      },
      links: {
        color: "#40E0D0",
        distance: 150,
        enable: true,
        opacity: 0.4,
        width: 1,
      },
      collisions: {
        enable: true,
      },
      move: {
        direction: "none",
        enable: true,
        outModes: {
          default: "bounce",
        },
        random: true,
        speed: 1,
        straight: false,
      },
      number: {
        density: {
          enable: true,
          area: 800,
        },
        value: 50,
      },
      opacity: {
        value: 0.5,
      },
      shape: {
        type: ["circle", "triangle"]
      },
      size: {
        value: { min: 1, max: 3 },
      },
    },
    detectRetina: true,
  };

  return (
    <div className="relative flex items-center justify-center min-h-screen bg-gradient-to-br from-[#0a192f] via-[#0c2a4a] to-[#0a3d62] overflow-hidden">
      {/* 粒子背景 */}
      <Particles 
        id="tsparticles" 
        init={particlesInit} 
        options={particlesOptions} 
        className="absolute inset-0 z-0"
      />
      
      {/* 底部波浪效果 */}
      <WaterWave className="z-10" />
      <WaterWave className="z-10 transform scale-110 translate-y-5 opacity-70" />
      
      {/* 海洋生物装饰元素 */}
      <div className="absolute bottom-10 left-10 z-10">
        <motion.div 
          className="text-5xl text-cyan-300"
          animate={{ y: [0, -10, 0] }}
          transition={{ duration: 3, repeat: Infinity }}
        >
          🐠
        </motion.div>
      </div>
      
      <div className="absolute top-10 right-10 z-10">
        <motion.div 
          className="text-4xl text-teal-300"
          animate={{ rotate: [0, 10, -10, 0] }}
          transition={{ duration: 8, repeat: Infinity }}
        >
          🦈
        </motion.div>
      </div>
      
      {/* 主卡片容器 */}
      <motion.div 
        className="relative z-20 bg-[rgba(10,25,47,0.8)] backdrop-blur-md border border-cyan-500/30 rounded-xl shadow-2xl shadow-cyan-500/20 p-10 w-full max-w-md transition-all duration-500"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        whileHover={{ scale: 1.02 }}
      >
        {/* 卡片发光效果 */}
        <div className="absolute inset-0 rounded-xl bg-cyan-500/10 blur-xl -z-10"></div>
        
        {/* 顶部装饰 */}
        <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-transparent via-cyan-400 to-transparent"></div>
        
        {/* 标题区域 */}
        <div className="text-center mb-8 relative">
          <div className="absolute -top-10 left-1/2 transform -translate-x-1/2">
            <div className="bg-cyan-500/10 p-3 rounded-full border border-cyan-400/30">
              <div className="text-3xl text-cyan-400">
                {isLoginMode ? '🔑' : '📝'}
              </div>
            </div>
          </div>
          
          <motion.h1 
            className="text-3xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-300 to-teal-300"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            {isLoginMode ? '欢迎登录 AquaAI' : '注册 AquaAI 账户'}
          </motion.h1>
          
          <motion.p 
            className="text-cyan-200 mt-2"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.3 }}
          >
            {isLoginMode ? '探索智能海洋养殖的无限可能' : '加入我们，开启智慧渔业之旅'}
          </motion.p>
        </div>
        
        {/* 表单区域 */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4 }}
        >
          {isLoginMode ? (
            <LoginForm switchToRegister={() => setIsLoginMode(false)} />
          ) : (
            <RegisterForm switchToLogin={() => setIsLoginMode(true)} />
          )}
        </motion.div>
        
        {/* 模式切换区域 */}
        <motion.div 
          className="mt-6 text-center text-sm text-cyan-200"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5 }}
        >
          {isLoginMode ? (
            <p>
              还没有账户？{' '}
              <button 
                onClick={() => setIsLoginMode(false)}
                className="font-medium text-teal-300 hover:text-teal-200 transition-colors underline underline-offset-4 decoration-cyan-500/50 hover:decoration-cyan-300"
              >
                立即注册
              </button>
            </p>
          ) : (
            <p>
              已有账户？{' '}
              <button 
                onClick={() => setIsLoginMode(true)}
                className="font-medium text-teal-300 hover:text-teal-200 transition-colors underline underline-offset-4 decoration-cyan-500/50 hover:decoration-cyan-300"
              >
                立即登录
              </button>
            </p>
          )}
        </motion.div>
        
        {/* 底部装饰 */}
        <div className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-transparent via-teal-400 to-transparent"></div>
      </motion.div>
      
      {/* 水波纹效果 */}
      <div className="absolute bottom-0 left-0 w-full h-40 bg-gradient-to-t from-cyan-500/10 to-transparent z-0"></div>
      
      {/* 版权信息 */}
      <div className="absolute bottom-4 left-0 right-0 text-center text-cyan-300/50 text-xs z-10">
        AquaAI 智能海洋养殖平台 © {new Date().getFullYear()}
      </div>
    </div>
  );
};

