import React, { useState, useEffect } from 'react';
import axios from 'axios';

// 可视化容器组件
export default function UnderwaterVisualization() {
  const [loading, setLoading] = useState(false);
  const [options, setOptions] = useState({});
  const [queryParams, setQueryParams] = useState({});
  const [charts, setCharts] = useState({});

  // 初始化选项
  useEffect(() => {
    axios.get('/api/visual/init-options')
      .then(res => setOptions(res.data))
      .catch(err => console.error('初始化失败:', err));
  }, []);

  // 处理参数变更
  const handleParamChange = (type, value) => {
    setQueryParams(prev => ({
      ...prev,
      [type]: value,
      // 重置下级参数
      ...(type === 'province' && { basin: null, location: null }),
      ...(type === 'basin' && { location: null })
    }));
  };

  // 提交查询
  const handleSubmit = async () => {
    setLoading(true);
    try {
      const res = await axios.post('/api/visual/query', {
        type: queryParams.mode,
        ...queryParams
      });
      setCharts(res.data);
    } finally {
      setLoading(false);
    }
  };

  // 渲染级联选择器
  const renderSelector = () => (
    <div className="visual-selector">
      {/* 模式选择 */}
      <select 
        value={queryParams.mode || ''}
        onChange={e => handleParamChange('mode', e.target.value)}
      >
        <option value="">选择查看方式</option>
        {options.modes?.map(mode => (
          <option key={mode} value={mode}>{mode === 'local' ? '本地水质' : '区域水质'}</option>
        ))}
      </select>

      {/* 动态内容 */}
      {queryParams.mode === 'local' && (
        <select
          value={queryParams.date || ''}
          onChange={e => handleParamChange('date', e.target.value)}
        >
          <option value="">选择日期</option>
          {options.date_range?.map(date => (
            <option key={date} value={date}>{date}</option>
          ))}
        </select>
      )}

      {queryParams.mode === 'regional' && (
        <>
          <select
            value={queryParams.province || ''}
            onChange={e => handleParamChange('province', e.target.value)}
          >
            <option value="">选择省份</option>
            {Object.keys(options.region_structure || {}).map(p => (
              <option key={p} value={p}>{p}</option>
            ))}
          </select>

          {queryParams.province && (
            <select
              value={queryParams.basin || ''}
              onChange={e => handleParamChange('basin', e.target.value)}
            >
              <option value="">选择流域</option>
              {Object.keys(options.region_structure[queryParams.province] || {}).map(b => (
                <option key={b} value={b}>{b}</option>
              ))}
            </select>
          )}

          {queryParams.basin && (
            <select
              value={queryParams.location || ''}
              onChange={e => handleParamChange('location', e.target.value)}
            >
              <option value="">选择监测点</option>
              {(options.region_structure[queryParams.province]?.[queryParams.basin] || []).map(l => (
                <option key={l} value={l}>{l}</option>
              ))}
            </select>
          )}
        </>
      )}

      <button 
        onClick={handleSubmit}
        // disabled={!validateParams()}
      >
        生成报告
      </button>
    </div>
  );

  return (
    <div className="visual-container">
      {renderSelector()}

      {loading && <div className="loading-indicator">数据加载中...</div>}

      <div className="chart-area">
        {charts.line && (
          <img src={`data:image/png;base64,${charts.line}`} 
               alt="水质变化趋势" />
        )}
        {charts.pie && (
          <img src={`data:image/png;base64,${charts.pie}`}
               alt="水质类别分布" />
        )}
      </div>
    </div>
  );
}
