import React, { useState, useEffect } from 'react';
import { Select, DatePicker, Spin } from 'antd';
import WaterQualityVisualization from '../components/WaterQualityVisualization';
import LoadingSpinner from '../components/LoadingSpinner';

const { Option } = Select;

export default function Underwater() {
  const [dataType, setDataType] = useState('local');
  const [dates, setDates] = useState([]);
  const [regions, setRegions] = useState({});
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedProvince, setSelectedProvince] = useState(null);
  const [selectedRiver, setSelectedRiver] = useState(null);
  const [selectedArea, setSelectedArea] = useState(null);
  const [charts, setCharts] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // 获取初始数据
    fetch('/api/water/available-dates')
      .then(res => res.json())
      .then(setDates);

    fetch('/api/water/regional-structure')
      .then(res => res.json())
      .then(setRegions);
  }, []);

  const fetchData = () => {
    setLoading(true);
    let url = '';
    if (dataType === 'local') {
      url = `/api/water/local?date=${selectedDate}`;
    } else {
      url = `/api/water/regional?province=${selectedProvince}&river=${selectedRiver}&area=${selectedArea}&date=${selectedDate}`;
    }

    fetch(url)
      .then(res => res.json())
      .then(data => {
        setCharts(data.charts);
        setLoading(false);
      });
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>水下监测系统</h2>

      <div style={{ marginBottom: 20 }}>
        <Select 
          defaultValue="local"
          onChange={setDataType}
          style={{ width: 200 }}
        >
          <Option value="local">本地水质</Option>
          <Option value="regional">各地水质</Option>
        </Select>
      </div>

      {dataType === 'local' ? (
        <LocalSelector 
          dates={dates}
          onDateChange={setSelectedDate}
          onSearch={fetchData}
        />
      ) : (
        <RegionalSelector
          regions={regions}
          onProvinceChange={setSelectedProvince}
          onRiverChange={setSelectedRiver}
          onAreaChange={setSelectedArea}
          onDateChange={setSelectedDate}
          onSearch={fetchData}
        />
      )}

      {loading ? <LoadingSpinner /> : <WaterQualityVisualization charts={charts} />}
    </div>
  );
}
