import { NavLink } from 'react-router-dom';
import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

const menuConfig = [
  { 
    title: '仪表盘', 
    path: '/', 
    icon: '📊',
    roles: ['user', 'operator', 'admin'],
    description: '系统概览视图'
  },
  { 
    title: '水下系统', 
    path: '/underwater', 
    icon: '🐋',
    roles: ['operator', 'admin'],
    description: '水下设备监控'
  },
  { 
    title: '智能中心', 
    path: '/ai-center', 
    icon: '🧠',
    roles: ['admin'],
    description: 'AI分析中心'
  },
  { 
    title: '管理界面', 
    path: '/admin', 
    icon: '⚙️',
    roles: ['admin'],
    description: '系统管理后台'
  }
];

export default function Sidebar({ isOpen, isMobile, onClose }) {
  const { authState } = useContext(AuthContext);

  // 修正权限字段为 currentRole
  const filteredMenu = menuConfig.filter(item => 
    item.roles.includes(authState.currentRole)
  );

  return (
    <>
      {/* 侧边栏主体 */}
      <aside style={{
        width: '240px',
        background: '#2c3e50',
        height: '100vh',
        position: 'fixed',
        left: isOpen ? 0 : '-240px', // 改用transform实现动画
        top: '65px', // 对齐导航栏高度
        transition: 'left 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
        zIndex: 1000,
        boxShadow: '2px 0 8px rgba(0,0,0,0.1)',
        overflowY: 'auto' // 添加滚动条
      }}>
        <div style={{ 
          padding: '24px 16px',
          height: 'calc(100% - 48px)' // 留出上下边距
        }}>
          {/* 标题区域 */}
          <div style={{ 
            padding: '0 8px 24px',
            borderBottom: '1px solid rgba(255,255,255,0.1)',
            marginBottom: '24px'
          }}>
            <div style={{ 
              display: 'flex',
              alignItems: 'center',
              gap: '12px',
              color: '#ecf0f1'
            }}>
              <div style={{
                width: '32px',
                height: '32px',
                background: 'rgba(255,255,255,0.1)',
                borderRadius: '8px',
                display: 'grid',
                placeItems: 'center'
              }}>
                🌊
              </div>
              <h3 style={{ 
                fontSize: '18px',
                fontWeight: 500,
                margin: 0
              }}>
                导航菜单
              </h3>
            </div>
          </div>

          {/* 菜单列表 */}
          <nav>
            <ul style={{ 
              listStyle: 'none', 
              padding: 0,
              margin: 0,
              display: 'grid',
              gap: '8px'
            }}>
              {filteredMenu.map(item => (
                <li key={item.path}>
                  <NavLink
                    to={item.path}
                    onClick={isMobile ? onClose : undefined}
                    style={({ isActive }) => ({
                      display: 'flex',
                      alignItems: 'center',
                      gap: '16px',
                      padding: '12px 16px',
                      borderRadius: '8px',
                      color: isActive ? '#3498db' : '#bdc3c7',
                      background: isActive ? 'rgba(52, 152, 219, 0.1)' : 'transparent',
                      textDecoration: 'none',
                      transition: 'all 0.2s',
                      ':hover': {
                        background: 'rgba(52, 152, 219, 0.05)'
                      }
                    })}
                  >
                    <span style={{ 
                      fontSize: '20px',
                      width: '24px',
                      textAlign: 'center'
                    }}>
                      {item.icon}
                    </span>
                    <div>
                      <div style={{ 
                        fontSize: '14px',
                        fontWeight: 500 
                      }}>
                        {item.title}
                      </div>
                      <div style={{ 
                        fontSize: '12px',
                        color: '#7f8c8d',
                        marginTop: '4px'
                      }}>
                        {item.description}
                      </div>
                    </div>
                  </NavLink>
                </li>
              ))}
            </ul>
          </nav>
        </div>
      </aside>

      {/* 移动端遮罩层 */}
      {isMobile && isOpen && (
        <div 
          style={{
            position: 'fixed',
            top: '65px',
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0,0,0,0.4)',
            zIndex: 999,
            backdropFilter: 'blur(2px)'
          }}
          onClick={onClose}
        />
      )}
    </>
  );
}
