import React, { useState } from 'react';

function Underwater() {
  const [filePath, setFilePath] = useState('');
  const [lineChart, setLineChart] = useState(null);
  const [pieChart, setPieChart] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/visualize-water', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ file_path: filePath })
      });

      if (!response.ok) {
        throw new Error(await response.text());
      }

      const data = await response.json();

      if (data.error) {
        throw new Error(data.error);
      }

      setLineChart(data.line_chart);
      setPieChart(data.pie_chart);
      setError('');
    } catch (err) {
      setError(err.message);
      setLineChart(null);
      setPieChart(null);
    }
  };

  return (
    <div className="underwater-container">
      <h1>水下数据可视化系统</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={filePath}
          onChange={(e) => setFilePath(e.target.value)}
          placeholder="输入数据文件路径（示例：data/water.json）"
          style={{ width: '300px' }}
        />
        <button type="submit">可视化</button>
      </form>

      {error && <div className="error">{error}</div>}

      <div className="charts-container">
        {lineChart && (
          <div className="chart">
            <h2>水质参数趋势图</h2>
            <img src={`data:image/png;base64,${lineChart}`} alt="水质趋势图" />
          </div>
        )}

        {pieChart && (
          <div className="chart">
            <h2>水质类别分布</h2>
            <img src={`data:image/png;base64,${pieChart}`} alt="水质分布图" />
          </div>
        )}
      </div>
    </div>
  );
}

export default Underwater;
