import React from 'react';

const parseChartType = (base64) => {
  const prefix = base64.slice(0, 30);
  if (prefix.includes('iVBORw0KGgo')) return 'line';
  if (prefix.includes('PHN2Zy')) return 'pie';
  return 'unknown';
};

export default ({ charts }) => (
  <div className="chart-container">
    {charts.map((chart, i) => (
      <div key={i} className={`chart-card ${parseChartType(chart)}-chart`}>
        <img
          src={`data:image/png;base64,${chart}`}
          alt={`水质图表 ${i + 1}`}
          style={{ 
            maxWidth: parseChartType(chart) === 'line' ? '100%' : '60%',
            margin: '20px auto',
            display: 'block'
          }}
        />
      </div>
    ))}
  </div>
);
