import React, { createContext, useContext, useState, useEffect } from 'react';

// 创建认证上下文
const AuthContext = createContext();

// 自定义 Hook，用于在组件中访问认证上下文
export const useAuth = () => useContext(AuthContext);

/**
 * AuthProvider 组件 - 认证状态管理提供者
 * 
 * 功能：
 * 1. 管理全局的用户认证状态（是否已登录、用户信息等）
 * 2. 提供登录、注册、登出功能
 * 3. 提供带认证的 fetch 方法（authFetch）
 * 4. 在应用启动时检查本地存储的登录状态
 * 
 * 状态说明：
 * - isAuthenticated: 用户是否已认证
 * - userInfo: 用户信息（用户名、邮箱、是否为管理员等）
 * - loading: 初始化检查认证状态时的加载状态
 */
export const AuthProvider = ({ children }) => {
  // 认证状态
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  // 用户信息
  const [userInfo, setUserInfo] = useState(null);
  // 加载状态
  const [loading, setLoading] = useState(true);

  /**
   * 组件挂载时检查本地存储的认证信息
   * 如果存在有效的 token 和用户信息，则自动登录
   */
  useEffect(() => {
    const checkAuth = async () => {
      // 从本地存储获取 token 和用户信息
      const token = localStorage.getItem('token');
      const user = localStorage.getItem('user');
      
      if (token && user) {
        try {
          // 验证 token 是否仍然有效
          const response = await fetch('/api/user', {
            headers: {
              'x-auth-token': token
            }
          });
          
          if (response.ok) {
            // token 有效，恢复登录状态
            setIsAuthenticated(true);
            setUserInfo(JSON.parse(user));
          } else {
            // token 无效，清除本地存储
            localStorage.removeItem('token');
            localStorage.removeItem('user');
          }
        } catch (error) {
          console.error('验证令牌时出错:', error);
          // 发生错误时清除本地存储
          localStorage.removeItem('token');
          localStorage.removeItem('user');
        }
      }
      
      // 检查完成，设置 loading 为 false
      setLoading(false);
    };
    
    checkAuth();
  }, []);

  /**
   * 登录方法
   * @param {string} username - 用户名
   * @param {string} password - 密码
   * @param {boolean} isAdmin - 是否以管理员身份登录
   * @returns {Promise} 登录结果
   */
  const login = async (username, password, isAdmin) => {
    try {
      // 发送登录请求
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password, isAdmin }),
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || '登录失败');
      }
      
      // 保存 token 到本地存储
      if (data.token) {
        localStorage.setItem('token', data.token);
      }
      
      // 保存用户信息到本地存储
      localStorage.setItem('user', JSON.stringify(data.user));
      
      // 更新认证状态
      setIsAuthenticated(true);
      setUserInfo(data.user);
      
      return data;
    } catch (error) {
      throw error;
    }
  };

  /**
   * 注册方法
   * @param {string} username - 用户名
   * @param {string} email - 邮箱
   * @param {string} password - 密码
   * @param {boolean} isAdmin - 是否注册为管理员
   * @param {string} adminCode - 管理员注册码（仅管理员注册时需要）
   * @returns {Promise} 注册结果
   */
  const register = async (username, email, password, isAdmin, adminCode) => {
    try {
      // 构建注册数据
      const userData = { username, email, password, isAdmin };
      
      // 如果是管理员注册，添加管理员代码
      if (isAdmin && adminCode) {
        userData.adminCode = adminCode;
      }
      
      // 发送注册请求
      const response = await fetch('/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });
      
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || '注册失败');
      }
      
      return data;
    } catch (error) {
      throw error;
    }
  };

  /**
   * 登出方法
   * 清除本地存储的认证信息，并向服务器发送登出请求
   */
  const logout = async () => {
    try {
      const token = localStorage.getItem('token');
      
      // 如果有 token，向服务器发送登出请求
      if (token) {
        await fetch('/api/logout', {
          method: 'POST',
          headers: {
            'x-auth-token': token
          }
        });
      }
    } catch (error) {
      console.error('登出请求失败:', error);
    } finally {
      // 无论请求是否成功，都清除本地认证信息
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      setIsAuthenticated(false);
      setUserInfo(null);
    }
  };

  /**
   * 带认证的 fetch 方法
   * 自动添加认证头，处理 401 未授权响应
   * @param {string} url - 请求 URL
   * @param {Object} options - fetch 选项
   * @returns {Promise} fetch 响应
   */
  const authFetch = async (url, options = {}) => {
    const token = localStorage.getItem('token');
    
    if (!token) {
      throw new Error('未授权');
    }
    
    // 在请求头中添加认证 token
    const headers = {
      ...options.headers,
      'x-auth-token': token
    };
    
    // 发送带认证头的请求
    const response = await fetch(url, {
      ...options,
      headers
    });
    
    // 如果返回 401，说明 token 已过期，自动登出
    if (response.status === 401) {
      logout();
      throw new Error('会话已过期，请重新登录');
    }
    
    return response;
  };

  // 提供给子组件的值
  const value = {
    isAuthenticated,  // 是否已认证
    userInfo,         // 用户信息
    loading,          // 加载状态
    login,            // 登录方法
    register,         // 注册方法
    logout,           // 登出方法
    authFetch         // 带认证的 fetch 方法
  };

  return (
    <AuthContext.Provider value={value}>
      {/* 只有在不加载时才渲染子组件 */}
      {!loading && children}
    </AuthContext.Provider>
  );
};
