import React, { useState, useRef, useEffect } from 'react';
import { Upload, Button, Card, Progress, Spin, Alert, Row, Col, Typography } from 'antd';
import { 
  UploadOutlined, 
  ReloadOutlined, 
  CloseOutlined, 
  RobotOutlined, 
  FishOutlined,
  SearchOutlined
} from '@ant-design/icons';
import './App.css';

const { Title, Text } = Typography;

function FishRecognitionPage() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [results, setResults] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [modelStatus, setModelStatus] = useState({ status: 'loading' });
  
  const fileInputRef = useRef(null);

  useEffect(() => {
    checkModelStatus();
  }, []);

  const checkModelStatus = async () => {
    try {
      const response = await fetch('http://localhost:3001/fish/status');
      const data = await response.json();
      if (!response.ok) {
        const text = await response.text();
        console.error('API响应错误:', text);
        throw new Error(`HTTP错误 ${response.status}: ${text.substring(0, 200)}`);
      }
      setModelStatus(data);
    } catch (error) {
      console.error('Error checking model status:', error);
      setModelStatus({ status: 'error', error: '无法连接到模型服务' });
    }
  };

  const handleImageChange = (info) => {
    const file = info.file;
    if (!file) return;
    
    setResults(null);
    setError(null);
    
    if (!file.type.match('image.*')) {
      setError('请选择图片文件');
      return;
    }
    
    if (file.size > 10 * 1024 * 1024) {
      setError('图片大小不能超过10MB');
      return;
    }
    
    setSelectedImage(file);
    
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviewImage(e.target.result);
    };
    reader.readAsDataURL(file);
  };
  
  const handleUpload = async () => {
    if (!selectedImage) {
      setError('请先选择图片');
      return;
    }
    
    setIsLoading(true);
    setError(null);
    
    try {
      const formData = new FormData();
      formData.append('image', selectedImage);
      
      const response = await fetch('http://localhost:3001/fish/identify', {
        method: 'POST',
        body: formData,
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || '识别失败');
      }
      
      const data = await response.json();
      setResults(data.results);
    } catch (err) {
      console.error('Error identifying fish:', err);
      setError(err.message || '识别过程中发生错误');
    } finally {
      setIsLoading(false);
    }
  };
  
  const triggerFileInput = () => {
    fileInputRef.current.click();
  };
  
  const resetForm = () => {
    setSelectedImage(null);
    setPreviewImage(null);
    setResults(null);
    setError(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  
  const getProgressColor = (percent) => {
    if (percent > 80) return '#52c41a';
    if (percent > 60) return '#a0d911';
    if (percent > 40) return '#faad14';
    return '#f5222d';
  };

  if (modelStatus.status === 'loading') {
    return (
      <div className="model-status-container">
        <div className="loading-content">
          <div className="loading-animation">
            <div className="ai-icon">
              <RobotOutlined />
            </div>
            <div className="bubbles">
              <div className="bubble"></div>
              <div className="bubble"></div>
              <div className="bubble"></div>
            </div>
          </div>
          <Title level={4} className="loading-text">正在检查鱼类识别模型状态...</Title>
          <Text type="secondary">初始化AI模型以进行海洋生物识别</Text>
        </div>
      </div>
    );
  }
  
  if (modelStatus.status === 'error' || modelStatus.status === 'unavailable') {
    return (
      <div className="model-status-container">
        <div className="error-content">
          <div className="error-icon">
            <div className="error-symbol">⚠️</div>
          </div>
          <Title level={4} style={{ color: '#ff4d4f', marginBottom: 16 }}>模型加载失败</Title>
          <Text style={{ color: '#ff4d4f', marginBottom: 24 }}>
            {modelStatus.error || '无法加载鱼类识别模型'}
          </Text>
          <Text type="secondary" style={{ marginBottom: 24 }}>
            请确保模型文件存在并且可以访问。检查后端服务器日志以获取更多信息。
          </Text>
          <Button 
            type="primary" 
            icon={<ReloadOutlined />}
            onClick={checkModelStatus}
            className="retry-btn"
          >
            重试
          </Button>
        </div>
      </div>
    );
  }
  
  return (
    <div className="fish-recognition-container">
      <div className="header">
        <Title level={2} className="app-title">
          <FishOutlined /> AI海洋鱼类识别系统
        </Title>
        <Text type="secondary" className="app-subtitle">
          基于深度学习的海洋生物识别技术
        </Text>
      </div>
      
      <Row gutter={24} className="main-content">
        <Col xs={24} md={12} className="upload-section">
          <Card 
            title="上传鱼类图片" 
            bordered={false}
            className="upload-card"
            headStyle={{ border: 'none', padding: '0 24px', fontSize: '18px' }}
            bodyStyle={{ padding: '24px' }}
          >
            <div className="upload-area">
              <Upload.Dragger
                name="file"
                accept="image/*"
                showUploadList={false}
                beforeUpload={() => false}
                onChange={handleImageChange}
                className={previewImage ? 'hidden' : 'visible'}
              >
                <div className="upload-placeholder">
                  <div className="ocean-icon">
                    <div className="wave"></div>
                    <FishOutlined className="fish-icon" />
                  </div>
                  <Title level={4} className="upload-text">
                    点击或拖拽上传鱼类图片
                  </Title>
                  <Text type="secondary">
                    支持 JPG, PNG 格式
                  </Text>
                </div>
              </Upload.Dragger>
              
              {previewImage && (
                <div className="image-preview-container">
                  <div className="image-wrapper">
                    <img
                      src={previewImage}
                      alt="鱼类图片预览"
                      className="preview-image"
                    />
                    <Button
                      icon={<CloseOutlined />}
                      onClick={resetForm}
                      className="reset-btn"
                      shape="circle"
                    />
                  </div>
                  
                  <Button
                    type="primary"
                    icon={<SearchOutlined />}
                    onClick={handleUpload}
                    disabled={isLoading}
                    className="identify-btn"
                    size="large"
                  >
                    {isLoading ? '识别中...' : '开始识别'}
                  </Button>
                </div>
              )}
            </div>
            
            {error && (
              <Alert
                message={error}
                type="error"
                showIcon
                className="error-alert"
              />
            )}
          </Card>
        </Col>
        
        <Col xs={24} md={12} className="results-section">
          <Card 
            title="识别结果" 
            bordered={false}
            className="results-card"
            headStyle={{ border: 'none', padding: '0 24px', fontSize: '18px' }}
            bodyStyle={{ padding: '24px' }}
          >
            <div className="results-content">
              {!results && !isLoading && (
                <div className="empty-results">
                  <div className="empty-icon">
                    <div className="search-icon">
                      <SearchOutlined />
                    </div>
                    <div className="bubble bubble-1"></div>
                    <div className="bubble bubble-2"></div>
                    <div className="bubble bubble-3"></div>
                  </div>
                  <Title level={4} className="empty-text">等待识别结果</Title>
                  <Text type="secondary">上传图片并开始识别以查看结果</Text>
                </div>
              )}
              
              {isLoading && (
                <div className="loading-results">
                  <Spin 
                    indicator={
                      <div className="ai-spinner">
                        <RobotOutlined className="spinner-icon" />
                      </div>
                    } 
                    tip="正在分析图片中的鱼类..."
                  />
                  <div className="loading-wave"></div>
                </div>
              )}
              
              {results && (
                <div className="results-data">
                  <div className="top-result">
                    <div className="result-header">
                      <Title level={4} className="result-title">
                        <span className="trophy-icon">🏆</span> 最可能的鱼类
                      </Title>
                      <Text className="confidence-value">
                        {results[0].confidence.toFixed(1)}% 匹配度
                      </Text>
                    </div>
                    <Title level={3} className="fish-name">
                      {results[0].fish_type}
                    </Title>
                    <Progress
                      percent={results[0].confidence}
                      strokeColor={getProgressColor(results[0].confidence)}
                      showInfo={false}
                      className="confidence-bar"
                    />
                  </div>
                  
                  <div className="other-results">
                    <Title level={5} className="sub-title">其他可能的结果</Title>
                    <div className="result-list">
                      {results.slice(1).map((result, index) => (
                        <div key={index} className="result-item">
                          <div className="fish-info">
                            <FishOutlined className="fish-icon-small" />
                            <Text className="fish-type">{result.fish_type}</Text>
                          </div>
                          <div className="confidence-container">
                            <Text className="confidence-percent">
                              {result.confidence.toFixed(1)}%
                            </Text>
                            <Progress
                              percent={result.confidence}
                              strokeColor={getProgressColor(result.confidence)}
                              showInfo={false}
                              className="small-bar"
                            />
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                  
                  <Button
                    icon={<ReloadOutlined />}
                    onClick={resetForm}
                    className="reset-button"
                  >
                    重新识别
                  </Button>
                </div>
              )}
            </div>
          </Card>
        </Col>
      </Row>
      
      <div className="footer">
        <Text type="secondary">AI海洋鱼类识别系统 v1.0 | 科技赋能海洋养殖</Text>
      </div>
    </div>
  );
}

export default FishRecognitionPage;
