import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const VideoList = () => {
  const [videos, setVideos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  // æ¨¡æ‹Ÿè§†é¢‘æ•°æ®
  const mockVideos = [
    "æµ·åº•çŠç‘šç¤ç›‘æµ‹.mp4",
    "æ·±æµ·é±¼ç±»è¡Œä¸ºç ”ç©¶.mp4",
    "æµ·æ´‹ç‰§åœºå…¨æ™¯.mp4",
    "æ°´ä¸‹è®¾å¤‡ç»´æŠ¤è®°å½•.mp4",
    "æµ·è‰åºŠç”Ÿæ€è§‚å¯Ÿ.mp4",
    "äººå·¥é±¼ç¤æŠ•æ”¾è®°å½•.mp4",
    "æµ·æ´‹ç”Ÿç‰©å¤šæ ·æ€§è°ƒæŸ¥.mp4",
    "æµ·æ°´è´¨é‡æ£€æµ‹è¿‡ç¨‹.mp4",
    "æµ·è±šç¾¤æ´»åŠ¨è®°å½•.mp4",
    "å¤œé—´æµ·åº•ç›‘æµ‹.mp4",
    "æ½œæ°´å‘˜ä½œä¸šè®°å½•.mp4",
    "æµ·åº•åœ°å½¢æµ‹ç»˜.mp4"
  ];

  // æ¨¡æ‹Ÿè§†é¢‘åˆ†ç±»
  const categories = [
    { id: 'all', name: 'å…¨éƒ¨è§†é¢‘' },
    { id: 'monitoring', name: 'ç¯å¢ƒç›‘æµ‹' },
    { id: 'research', name: 'ç§‘å­¦ç ”ç©¶' },
    { id: 'equipment', name: 'è®¾å¤‡è®°å½•' },
    { id: 'wildlife', name: 'æµ·æ´‹ç”Ÿç‰©' }
  ];

  useEffect(() => {
    // æ¨¡æ‹ŸAPIè¯·æ±‚
    const fetchVideos = async () => {
      try {
        setLoading(true);
        // æ¨¡æ‹Ÿç½‘ç»œå»¶è¿Ÿ
        await new Promise(resolve => setTimeout(resolve, 800));
        // å®é™…é¡¹ç›®ä¸­æ›¿æ¢ä¸ºçœŸå®APIè°ƒç”¨
        // const response = await fetch('http://localhost:3001/api/videos');
        // const data = await response.json();
        setVideos(mockVideos);
      } catch (error) {
        console.error('Error fetching videos:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchVideos();
  }, []);

  // è¿‡æ»¤è§†é¢‘
  const filteredVideos = videos.filter(video => {
    // åº”ç”¨æœç´¢è¿‡æ»¤
    if (searchTerm && !video.toLowerCase().includes(searchTerm.toLowerCase())) {
      return false;
    }
    
    // åº”ç”¨åˆ†ç±»è¿‡æ»¤
    if (filter === 'all') return true;
    if (filter === 'monitoring') return video.includes('ç›‘æµ‹') || video.includes('æ£€æµ‹');
    if (filter === 'research') return video.includes('ç ”ç©¶') || video.includes('è°ƒæŸ¥');
    if (filter === 'equipment') return video.includes('è®¾å¤‡') || video.includes('ç»´æŠ¤');
    if (filter === 'wildlife') return video.includes('ç”Ÿç‰©') || video.includes('é±¼ç±»') || video.includes('æµ·è±š');
    
    return true;
  });

  // è·å–è§†é¢‘ç¼©ç•¥å›¾URLï¼ˆæ¨¡æ‹Ÿï¼‰
  const getThumbnailUrl = (videoName) => {
    const thumbnails = [
      "https://images.unsplash.com/photo-1503376780353-7e6692767b70?q=80&w=300",
      "https://images.unsplash.com/photo-1536152470836-b943b246224c?q=80&w=300",
      "https://images.unsplash.com/photo-1544551763-46a013bb70d5?q=80&w=300",
      "https://images.unsplash.com/photo-1560275619-4662e36fa65c?q=80&w=300",
      "https://images.unsplash.com/photo-1516483638261-f4dbaf036963?q=80&w=300",
      "https://images.unsplash.com/photo-1540202404-1b927e27fa8b?q=80&w=300"
    ];
    
    // æ ¹æ®è§†é¢‘åç§°çš„å“ˆå¸Œå€¼é€‰æ‹©ç¼©ç•¥å›¾
    const hash = Array.from(videoName).reduce((acc, char) => acc + char.charCodeAt(0), 0);
    return thumbnails[hash % thumbnails.length];
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-[#0a192f] to-[#0a3d62]">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-cyan-400 mx-auto"></div>
          <p className="mt-4 text-cyan-200">æ­£åœ¨åŠ è½½è§†é¢‘æ•°æ®...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#0a192f] via-[#0c2a4a] to-[#0a3d62] relative overflow-hidden">
      {/* æµ·æ´‹èƒŒæ™¯è£…é¥° */}
      <div className="absolute inset-0 z-0">
        {[...Array(30)].map((_, i) => (
          <div 
            key={i}
            className="absolute rounded-full animate-pulse"
            style={{
              top: `${Math.random() * 100}%`,
              left: `${Math.random() * 100}%`,
              width: `${Math.random() * 10 + 2}px`,
              height: `${Math.random() * 10 + 2}px`,
              backgroundColor: i % 3 === 0 
                ? 'rgba(0, 255, 255, 0.3)' 
                : i % 3 === 1 
                  ? 'rgba(0, 191, 255, 0.3)' 
                  : 'rgba(30, 144, 255, 0.3)',
              opacity: Math.random() * 0.5 + 0.2,
              animationDuration: `${Math.random() * 5 + 3}s`,
            }}
          />
        ))}
        
        {/* æ³¢æµªæ•ˆæœ */}
        <div className="absolute bottom-0 left-0 w-full overflow-hidden">
          <svg 
            className="relative block w-[calc(100%+1.3px)] h-[80px]"
            viewBox="0 0 1200 120" 
            preserveAspectRatio="none"
          >
            <path 
              d="M0,0V46.29c47.79,22.2,103.59,32.17,158,28,70.36-5.37,136.33-33.31,206.8-37.5C438.64,32.43,512.34,53.67,583,72.05c69.27,18,138.3,24.88,209.4,13.08,36.15-6,69.85-17.84,104.45-29.34C989.49,25,1113-14.29,1200,52.47V0Z" 
              fill="rgba(255,255,255,0.1)" 
            ></path>
            <path 
              d="M0,0V15.81C13,36.92,27.64,56.86,47.69,72.05,99.41,111.27,165,111,224.58,91.58c31.15-10.15,60.09-26.07,89.67-39.8,40.92-19,84.73-46,130.83-49.67,36.26-2.85,70.9,9.42,98.6,31.56,31.77,25.39,62.32,62,103.63,73,40.44,10.79,81.35-6.69,119.13-24.28s75.16-39,116.92-43.05c59.73-5.85,113.28,22.88,168.9,38.84,30.2,8.66,59,6.17,87.09-7.5,22.43-10.89,48-26.93,60.65-49.24V0Z" 
              fill="rgba(255,255,255,0.2)" 
            ></path>
            <path 
              d="M0,0V5.63C149.93,59,314.09,71.32,475.83,42.57c43-7.64,84.23-20.12,127.61-26.46,59-8.63,112.48,12.24,165.56,35.4C827.93,77.22,886,95.24,951.2,90c86.53-7,172.46-45.71,248.8-84.81V0Z" 
              fill="rgba(255,255,255,0.3)" 
            ></path>
          </svg>
        </div>
      </div>
      
      {/* æµ·æ´‹ç”Ÿç‰©è£…é¥° */}
      <div className="absolute top-10 right-10 z-10">
        <motion.div 
          className="text-4xl text-cyan-300 opacity-50"
          animate={{ y: [0, -10, 0] }}
          transition={{ duration: 4, repeat: Infinity }}
        >
          ğŸ 
        </motion.div>
      </div>
      
      <div className="absolute bottom-20 left-10 z-10">
        <motion.div 
          className="text-5xl text-teal-300 opacity-50"
          animate={{ rotate: [0, 10, -10, 0] }}
          transition={{ duration: 8, repeat: Infinity }}
        >
          ğŸ¢
        </motion.div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8 relative z-10">
        {/* é¡µé¢æ ‡é¢˜ */}
        <motion.div 
          className="text-center mb-8"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1 className="text-3xl md:text-4xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-300 to-teal-300 mb-2">
            æµ·æ´‹ç‰§åœºç›‘æ§è§†é¢‘åº“
          </h1>
          <p className="text-cyan-200 max-w-2xl mx-auto">
            å®æ—¶ç›‘æµ‹æ°´ä¸‹ç¯å¢ƒï¼Œè®°å½•æµ·æ´‹ç”Ÿæ€å˜åŒ–ï¼ŒåŠ©åŠ›æµ·æ´‹ç‰§åœºç§‘å­¦ç®¡ç†
          </p>
        </motion.div>

        {/* æœç´¢å’Œç­›é€‰åŒºåŸŸ */}
        <motion.div 
          className="bg-[rgba(10,25,47,0.7)] backdrop-blur-md border border-cyan-500/30 rounded-xl p-6 mb-8 shadow-xl shadow-cyan-500/10"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.1 }}
        >
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-cyan-300 mb-2">æœç´¢è§†é¢‘</label>
              <div className="relative">
                <input
                  type="text"
                  placeholder="è¾“å…¥è§†é¢‘åç§°..."
                  className="w-full bg-[rgba(10,40,70,0.5)] border border-cyan-500/30 rounded-lg py-3 px-4 pl-10 text-cyan-200 focus:outline-none focus:ring-2 focus:ring-cyan-500"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <div className="absolute left-3 top-3.5 text-cyan-400">
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd" />
                  </svg>
                </div>
              </div>
            </div>
            
            <div>
              <label className="block text-cyan-300 mb-2">è§†é¢‘åˆ†ç±»</label>
              <div className="flex flex-wrap gap-2">
                {categories.map(category => (
                  <motion.button
                    key={category.id}
                    className={`px-4 py-2 rounded-full text-sm transition-all ${
