import { useState, useRef, useEffect } from 'react';

function FishRecognitionPage() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [results, setResults] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [modelStatus, setModelStatus] = useState({ status: 'loading' });
  
  const fileInputRef = useRef(null);

  useEffect(() => {
    checkModelStatus();
  }, []);

  const checkModelStatus = async () => {
    try {
      const response = await fetch('http://localhost:3001/fish/status');
      const data = await response.json();
      if (!response.ok) {
      // 尝试获取错误响应内容
      const text = await response.text();
      console.error('API响应错误:', text);
      throw new Error(`HTTP错误 ${response.status}: ${text.substring(0, 200)}`);
    }
      setModelStatus(data);
    } catch (error) {
      console.error('Error checking model status:', error);
      setModelStatus({ status: 'error', error: '无法连接到模型服务' });
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    
    setResults(null);
    setError(null);
    
    if (!file.type.match('image.*')) {
      setError('请选择图片文件');
      return;
    }
    
    if (file.size > 10 * 1024 * 1024) {
      setError('图片大小不能超过10MB');
      return;
    }
    
    setSelectedImage(file);
    
    const reader = new FileReader();
    reader.onload = (e) => {
      setPreviewImage(e.target.result);
    };
    reader.readAsDataURL(file);
  };
  
  const handleUpload = async () => {
    if (!selectedImage) {
      setError('请先选择图片');
      return;
    }
    
    setIsLoading(true);
    setError(null);
    
    try {
      const formData = new FormData();
      formData.append('image', selectedImage);
      
      const response = await fetch('/fish/identify', {
        method: 'POST',
        body: formData,
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || '识别失败');
      }
      
      const data = await response.json();
      setResults(data.results);
    } catch (err) {
      console.error('Error identifying fish:', err);
      setError(err.message || '识别过程中发生错误');
    } finally {
      setIsLoading(false);
    }
  };
  
  const triggerFileInput = () => {
    fileInputRef.current.click();
  };
  
  const resetForm = () => {
    setSelectedImage(null);
    setPreviewImage(null);
    setResults(null);
    setError(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };
  
  const renderConfidenceBar = (confidence) => {
    let color = 'bg-red-500';
    if (confidence > 80) {
      color = 'bg-green-500';
    } else if (confidence > 50) {
      color = 'bg-yellow-500';
    } else if (confidence > 30) {
      color = 'bg-orange-500';
    }
    
    return (
      <div className="w-full bg-gray-200 rounded-full h-2.5 mt-1">
        <div 
          className={`${color} h-2.5 rounded-full`} 
          style={{ width: `${confidence}%` }}
        ></div>
      </div>
    );
  };
  
  if (modelStatus.status === 'loading') {
    return (
      <div className="flex flex-col items-center justify-center h-full p-4">
        <div className="text-center">
          <div className="flex space-x-2 justify-center mb-4">
            <div className="h-3 w-3 bg-blue-500 rounded-full animate-bounce" style={{animationDelay: '-0.32s'}}></div>
            <div className="h-3 w-3 bg-blue-500 rounded-full animate-bounce" style={{animationDelay: '-0.16s'}}></div>
            <div className="h-3 w-3 bg-blue-500 rounded-full animate-bounce"></div>
          </div>
          <p>正在检查鱼类识别模型状态...</p>
        </div>
      </div>
    );
  }
  
  if (modelStatus.status === 'error' || modelStatus.status === 'unavailable') {
    return (
      <div className="flex flex-col items-center justify-center h-full p-4">
        <div className="text-center bg-red-50 p-6 rounded-lg border border-red-200 max-w-md">
          <div className="text-4xl mb-4">⚠️</div>
          <h2 className="text-xl font-bold text-red-700 mb-2">模型加载失败</h2>
          <p className="text-red-600 mb-4">{modelStatus.error || '无法加载鱼类识别模型'}</p>
          <p className="text-gray-600 text-sm">请确保模型文件存在并且可以访问。检查后端服务器日志以获取更多信息。</p>
          <button 
            onClick={checkModelStatus}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
          >
            重试
          </button>
        </div>
      </div>
    );
  }
  
  return (
    <div className="flex flex-col h-full p-4 bg-gray-50">
      <div className="flex-1 flex flex-col md:flex-row gap-6">
        <div className="flex-1 flex flex-col items-center justify-center bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleImageChange}
            accept="image/*"
            className="hidden"
          />
          
          {!previewImage ? (
            <div 
              onClick={triggerFileInput}
              className="border-2 border-dashed border-blue-300 rounded-lg p-8 w-full h-64 flex flex-col items-center justify-center cursor-pointer hover:border-blue-500 transition"
            >
              <div className="text-4xl mb-4">🐟</div>
              <p className="text-lg text-gray-600 mb-2">点击或拖拽上传鱼类图片</p>
              <p className="text-sm text-gray-500">支持 JPG, PNG 格式</p>
            </div>
          ) : (
            <div className="w-full flex flex-col items-center">
              <div className="relative w-full max-w-md">
                <img
                  src={previewImage}
                  alt="鱼类图片预览"
                  className="w-full h-auto rounded-lg shadow-md"
                />
                <button
                  onClick={resetForm}
                  className="absolute top-2 right-2 bg-white rounded-full p-1 shadow-md hover:bg-gray-100"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              
              <button
                onClick={handleUpload}
                disabled={isLoading}
                className={`mt-4 px-6 py-2 rounded-lg font-medium ${
                  isLoading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
                } text-white`}
              >
                {isLoading ? (
                  <div className="flex items-center">
                    <div className="animate-spin rounded-full h-4 w-4 border-t-2 border-white mr-2"></div>
                    识别中...
                  </div>
                ) : '开始识别'}
              </button>
            </div>
          )}
          
          {error && (
            <div className="mt-4 w-full bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
              <p>{error}</p>
            </div>
          )}
        </div>
        
        <div className="flex-1 bg-white p-6 rounded-lg shadow-sm border border-gray-200">
          <h2 className="text-xl font-bold text-gray-800 mb-4">识别结果</h2>
          
          {!results && !isLoading && (
            <div className="flex flex-col items-center justify-center h-64 text-gray-500">
              <div className="text-4xl mb-4">🔍</div>
              <p>上传图片并开始识别以查看结果</p>
            </div>
          )}
          
          {isLoading && (
            <div className="flex flex-col items-center justify-center h-64">
              <div className="flex space-x-2 justify-center mb-4">
                <div className="h-3 w-3 bg-blue-500 rounded-full animate-bounce" style={{animationDelay: '-0.32s'}}></div>
                <div className="h-3 w-3 bg-blue-500 rounded-full animate-bounce" style={{animationDelay: '-0.16s'}}></div>
                <div className="h-3 w-3 bg-blue-500 rounded-full animate-bounce"></div>
              </div>
              <p className="text-gray-600">正在分析图片中的鱼类...</p>
            </div>
          )}
          
          {results && (
            <div className="space-y-6">
              <div className="bg-blue-50 p-4 rounded-lg border border-blue-100">
                <div className="flex items-center space-x-2 mb-1">
                  <span className="text-lg font-semibold text-blue-800">🏆 最可能的鱼类</span>
                  <span className="bg-blue-600 text-white px-2 py-0.5 rounded-full text-xs">
                    {results[0].confidence.toFixed(1)}% 匹配度
                  </span>
                </div>
                <h3 className="text-2xl font-bold text-blue-900">{results[0].fish_type}</h3>
                {renderConfidenceBar(results[0].confidence)}
              </div>
              
              <div className="mt-4">
                <h3 className="text-md font-semibold text-gray-700 mb-2">其他可能的结果</h3>
                <div className="space-y-3">
                  {results.slice(1).map((result, index) => (
                    <div key={index} className="p-3 bg-gray-50 rounded-lg border border-gray-200">
                      <div className="flex justify-between items-center">
                        <span className="font-medium text-gray-800">{result.fish_type}</span>
                        <span className="text-sm text-gray-500">{result.confidence.toFixed(1)}%</span>
                      </div>
                      {renderConfidenceBar(result.confidence)}
                    </div>
                  ))}
                </div>
              </div>
              
              <div className="mt-6 pt-4 border-t border-gray-200">
                <button
                  onClick={resetForm}
                  className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition"
                >
                  重新识别
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default FishRecognitionPage;
