import React, { useState, useEffect, useRef } from 'react';
import { Card, Row, Col, Select, Spin, message, Button, Space } from 'antd';
import { Bar, Scatter } from 'react-chartjs-2';
import Chart from 'chart.js/auto';
import { DownloadOutlined } from '@ant-design/icons';

const { Option } = Select;

function FishDataVisualization() {
  const [fishData, setFishData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedSpecies, setSelectedSpecies] = useState('');
  const [availableSpecies, setAvailableSpecies] = useState([]);
  const [isDownloading, setIsDownloading] = useState(false);
  
  // 按钮引用
  const pngButtonRef = useRef(null);
  const pdfButtonRef = useRef(null);
  
  // 图表引用
  const weightBarChartRef = useRef(null);
  const generalScatterChartRef = useRef(null);
  const singleSpeciesChartRef = useRef(null);

  // 组件挂载日志
  useEffect(() => {
    console.log("FishDataVisualization组件已挂载");
    
    // 原生事件绑定测试
    if (pngButtonRef.current) {
      pngButtonRef.current.addEventListener('click', () => {
        console.log("原生事件测试 - PNG按钮");
      });
    }
    
    if (pdfButtonRef.current) {
      pdfButtonRef.current.addEventListener('click', () => {
        console.log("原生事件测试 - PDF按钮");
      });
    }
  }, [pngButtonRef, pdfButtonRef]);

  // 获取鱼类数据
  useEffect(() => {
    console.log("组件依赖更新，获取鱼类数据");
    fetchFishData();
  }, [selectedSpecies]);

  const fetchFishData = async () => {
    setIsLoading(true);
    try {
      const formData = new FormData();
      if (selectedSpecies) {
        formData.append('species', selectedSpecies);
      }

      const response = await fetch('http://localhost:3001/visualize-fish', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) throw new Error(await response.text());

      const data = await response.json();
      setFishData(data);

      // 提取鱼种列表
      if (data.bar_chart_data) {
        setAvailableSpecies(data.bar_chart_data.map(item => item.Species));
      }

    } catch (err) {
      console.error('获取数据失败:', err);
      message.error(err.message || '获取数据失败，请检查网络');
    } finally {
      setIsLoading(false);
    }
  };

  // 渲染柱状图
  const renderWeightBarChart = () => {
    if (!fishData || !fishData.bar_chart_data) return null;

    const { bar_chart_data } = fishData;
    const labels = bar_chart_data.map(item => item.Species);
    const values = bar_chart_data.map(item => item['Weight(g)']);

    return (
      <Card 
        title={
          <div className="flex justify-between items-center">
            <span>每种鱼类的平均重量</span>
            <Space>
              <Button 
                ref={pngButtonRef}
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => {
                  // 简化的点击测试
                  console.log("PNG下载按钮被点击 - 合成事件");
                }}
                loading={isDownloading}
                style={{ border: '2px solid blue', backgroundColor: 'rgba(255, 255, 0, 0.3)' }} // 醒目的样式
              >
                PNG
              </Button>
              <Button 
                ref={pdfButtonRef}
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => {
                  console.log("PDF下载按钮被点击 - 合成事件");
                }}
                loading={isDownloading}
                style={{ border: '2px solid blue', backgroundColor: 'rgba(255, 255, 0, 0.3)' }} // 醒目的样式
              >
                PDF
              </Button>
            </Space>
          </div>
        }
        bordered={true}
      >
        <div style={{ height: '350px' }}>
          <Bar
            ref={weightBarChartRef}
            data={{
              labels: labels,
              datasets: [{
                label: '平均重量 (g)',
                data: values,
                backgroundColor: [
                  'rgba(54, 162, 235, 0.7)',
                  'rgba(255, 99, 132, 0.7)',
                  'rgba(255, 206, 86, 0.7)',
                  'rgba(75, 192, 192, 0.7)',
                  'rgba(153, 102, 255, 0.7)',
                  'rgba(255, 159, 64, 0.7)',
                  'rgba(173, 216, 230, 0.7)'
                ],
                borderColor: [
                  'rgb(54, 162, 235)',
                  'rgb(255, 99, 132)',
                  'rgb(255, 206, 86)',
                  'rgb(75, 192, 192)',
                  'rgb(153, 102, 255)',
                  'rgb(255, 159, 64)',
                  'rgb(173, 216, 230)'
                ],
                borderWidth: 1
              }]
            }}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: { position: 'top' }
              },
              scales: { y: { beginAtZero: true, title: { text: '平均重量 (g)' } } }
            }}
          />
        </div>
      </Card>
    );
  };

  // 其余渲染函数...（与之前相同，此处省略以简化代码）

  return (
    <div style={{ padding: '20px' }} onClick={(e) => console.log("页面点击事件", e.target)}>
      <div className="flex justify-between items-center mb-6">
        <h1 style={{ color: '#1890ff', fontSize: '2rem', fontWeight: '600' }}>
          鱼类数据可视化分析
        </h1>
        <Button 
          type="primary" 
          icon={<DownloadOutlined />} 
          onClick={() => {
            console.log("下载全部图表按钮被点击");
          }}
          loading={isDownloading || isLoading}
        >
          下载全部图表
        </Button>
      </div>

      <Spin spinning={isLoading} tip="加载鱼类数据...">
        <div style={{ marginBottom: '30px', textAlign: 'center' }}>
          <Select
            value={selectedSpecies}
            onChange={handleSpeciesChange}
            style={{ width: '300px', height: '52px', fontSize: '16px' }}
            placeholder="选择鱼种查看详细分析"
          >
            <Option value="">全部鱼类</Option>
            {availableSpecies.map((species, index) => (
              <Option key={index} value={species}>{species}</Option>
            ))}
          </Select>
        </div>

        <Row gutter={16}>
          <Col xs={24} md={12}>
            {renderWeightBarChart()}
          </Col>
          <Col xs={24} md={12}>
            {renderGeneralScatterChart()}
          </Col>
        </Row>

        {selectedSpecies && renderSingleSpeciesScatterChart()}
      </Spin>
    </div>
  );
}

export default FishDataVisualization;
