import { Link, useNavigate } from 'react-router-dom';
import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

export default function Navbar({ isMobile, onToggleSidebar }) {
  const { authState } = useContext(AuthContext);
  const navigate = useNavigate();

  // 获取角色显示名称
  const getRoleLabel = () => {
    const roleMap = {
      admin: '系统管理员',
      operator: '操作员',
      user: '普通用户'
    };
    return roleMap[authState.currentRole] || '未知角色';
  };

  return (
    <nav style={{
      background: '#2c3e50',
      color: 'white',
      padding: '15px 20px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      position: 'sticky',
      top: 0,
      zIndex: 1000,
      boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
    }}>
      {/* 左侧导航区 */}
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        gap: '15px',
        flex: 1
      }}>
        {isMobile && (
          <button 
            onClick={onToggleSidebar}
            style={mobileMenuButtonStyle}
            aria-label="切换菜单"
          >
            ☰
          </button>
        )}
        <Link to="/" style={logoStyle}>
          OCEAN SYSTEM
        </Link>
      </div>

      {/* 右侧用户区 */}
      <div style={userPanelStyle}>
        <div style={userBadgeStyle}>
          <div style={roleIconStyle}>
            {authState.currentRole[0].toUpperCase()}
          </div>
          <span style={roleLabelStyle}>
            {getRoleLabel()}
          </span>
        </div>
      </div>
    </nav>
  );
}

// 样式常量
const mobileMenuButtonStyle = {
  background: 'rgba(255,255,255,0.1)',
  border: 'none',
  color: 'white',
  fontSize: '24px',
  cursor: 'pointer',
  padding: '4px 8px',
  borderRadius: '4px',
  transition: 'background 0.2s',
  ':hover': {
    background: 'rgba(255,255,255,0.2)'
  }
};

const logoStyle = {
  textDecoration: 'none', 
  color: 'white',
  fontSize: 'clamp(1rem, 2vw, 1.25rem)',
  fontWeight: '600',
  whiteSpace: 'nowrap',
  marginLeft: '8px'
};

const userPanelStyle = {
  display: 'flex', 
  alignItems: 'center', 
  gap: '1rem',
  position: 'relative',
  flex: 0
};

const userBadgeStyle = {
  display: 'flex',
  alignItems: 'center',
  gap: '12px',
  background: 'rgba(255,255,255,0.1)',
  padding: '6px 12px 6px 6px',
  borderRadius: '24px',
  transition: 'all 0.2s',
  ':hover': {
    background: 'rgba(255,255,255,0.15)'
  }
};

const roleIconStyle = {
  width: '36px',
  height: '36px',
  borderRadius: '50%',
  background: '#3498db',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  fontSize: '18px',
  fontWeight: '500'
};

const roleLabelStyle = {
  fontSize: '0.875rem',
  textTransform: 'uppercase',
  letterSpacing: '0.5px'
};
