import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

const VideoPlatform = () => {
  const [videos, setVideos] = useState([]);
  const [currentVideo, setCurrentVideo] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [progress, setProgress] = useState(0);
  const [showControls, setShowControls] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const videoRef = useRef(null);
  const progressRef = useRef(null);
  const containerRef = useRef(null);

  // 获取视频列表（对接后端API）
  useEffect(() => {
    const fetchVideos = async () => {
      try {
        setLoading(true);
        // 替换为你的实际视频列表API端点
        const response = await axios.get('/api/videos');
        setVideos(response.data);
      } catch (err) {
        setError('无法加载视频列表');
      } finally {
        setLoading(false);
      }
    };

    fetchVideos();
  }, []);

  // 视频控制逻辑
  const handlePlayPause = () => {
    if (!videoRef.current) return;
    
    if (videoRef.current.paused) {
      videoRef.current.play().catch(err => console.log('自动播放被阻止'));
      setIsPlaying(true);
    } else {
      videoRef.current.pause();
      setIsPlaying(false);
    }
  };

  // 其他控制逻辑保持不变...

  // 视频加载处理
  const handleVideoLoad = () => {
    if (videoRef.current) {
      videoRef.current.volume = volume;
      videoRef.current.muted = isMuted;
    }
  };

  // 错误处理
  const handleVideoError = (e) => {
    setError('视频加载失败');
    console.error('视频错误:', e);
  };

  return (
    <div className="bg-gray-100 min-h-screen font-sans">
      {/* 导航栏 */}
      <nav className="fixed top-0 left-0 right-0 bg-white shadow-md z-50">
        {/* 导航内容 */}
      </nav>

      <main className="container mx-auto px-4 pt-24 pb-12">
        {/* 加载状态 */}
        {loading && (
          <div className="text-center py-8">
            <div className="loader ease-linear rounded-full border-4 border-t-4 border-primary h-12 w-12 mb-4 mx-auto"></div>
            <p>加载中...</p>
          </div>
        )}

        {/* 错误提示 */}
        {error && (
          <div className="text-center text-red-500 py-8">
            {error}
          </div>
        )}

        {/* 视频列表 */}
        {!loading && !error && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {videos.map(video => (
              <VideoCard
                key={video.id}
                video={video}
                onClick={() => setCurrentVideo(video)}
              />
            ))}
          </div>
        )}

        {/* 视频播放页 */}
        {currentVideo && (
          <div className="fixed top-0 left-0 right-0 bottom-0 bg-black z-50">
            {/* 返回按钮 */}
            <button
              onClick={() => setCurrentVideo(null)}
              className="absolute top-4 left-4 text-white z-10 p-2 rounded hover:bg-gray-800"
            >
              ← 返回
            </button>

            <div className="relative h-full max-w-7xl mx-auto">
              {/* 视频容器 */}
              <div className="video-container aspect-video mt-16">
                <video
                  ref={videoRef}
                  src={currentVideo.source} // 确保后端返回正确的视频URL
                  poster={currentVideo.cover}
                  className="w-full h-full object-cover"
                  onTimeUpdate={handleTimeUpdate}
                  onLoadedMetadata={handleVideoLoad}
                  onError={handleVideoError}
                />

                {/* 控制条 */}
                {showControls && (
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
                    {/* 进度条 */}
                    <div
                      ref={progressRef}
                      className="w-full h-1 bg-gray-600 rounded-full cursor-pointer mb-3"
                      onClick={handleProgressClick}
                    >
                      <div
                        className="h-full bg-primary rounded-full"
                        style={{ width: `${progress}%` }}
                      />
                    </div>

                    {/* 控制按钮 */}
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        {/* 播放/暂停按钮 */}
                        <button onClick={handlePlayPause} className="p-2 rounded-full bg-black/50 hover:bg-primary/80">
                          {isPlaying ? (
                            <i className="fa fa-pause text-white text-xl" />
                          ) : (
                            <i className="fa fa-play text-white text-xl" />
                          )}
                        </button>

                        {/* 音量控制 */}
                        <div className="flex items-center space-x-2">
                          <button onClick={handleMute} className="p-2 rounded-full bg-black/50 hover:bg-primary/80">
                            {isMuted ? (
                              <i className="fa fa-volume-off text-white text-xl" />
                            ) : volume > 0.5 ? (
                              <i className="fa fa-volume-up text-white text-xl" />
                            ) : (
                              <i className="fa fa-volume-down text-white text-xl" />
                            )}
                          </button>
                          <input
                            type="range"
                            min="0"
                            max="1"
                            step="0.05"
                            value={volume}
                            onChange={handleVolumeChange}
                            className="volume-slider"
                          />
                        </div>

                        {/* 时间显示 */}
                        <div className="text-white text-sm">
                          {formatTime(videoRef.current?.currentTime || 0)} / 
                          {formatTime(videoRef.current?.duration || 0)}
                        </div>
                      </div>

                      {/* 全屏按钮 */}
                      <button onClick={handleFullscreen} className="p-2 rounded-full bg-black/50 hover:bg-primary/80">
                        <i className="fa fa-expand text-white text-xl" />
                      </button>
                    </div>
                  </div>
                )}
              </div>

              {/* 视频信息 */}
              <div className="p-6 text-white">
                <h1 className="text-2xl font-bold mb-3">{currentVideo.title}</h1>
                <div className="flex items-center justify-between">
                  {/* UP主信息 */}
                  <div className="flex items-center space-x-4">
                    <img src={currentVideo.channel.avatar} alt="UP主头像" className="w-8 h-8 rounded-full" />
                    <span>{currentVideo.channel.name}</span>
                  </div>
                  {/* 观看次数 */}
                  <div className="flex items-center space-x-2">
                    <i className="fa fa-eye" />
                    <span>{currentVideo.views}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

// 视频卡片组件（优化版）
const VideoCard = ({ video, onClick }) => (
  <div 
    className="bg-white rounded-xl shadow-md overflow-hidden cursor-pointer transition-transform hover:scale-105"
    onClick={onClick}
  >
    <img 
      src={video.cover} 
      alt={video.title} 
      className="w-full aspect-video object-cover"
      loading="lazy"
    />
    <div className="p-4">
      <h3 className="font-medium line-clamp-2 mb-2">{video.title}</h3>
      <div className="flex items-center justify-between text-sm text-gray-600">
        <div className="flex items-center space-x-1 truncate">
          <img src={video.channel.avatar} alt="UP主头像" className="w-5 h-5 rounded-full" />
          <span>{video.channel.name}</span>
        </div>
        <div className="flex items-center space-x-1">
          <i className="fa fa-eye" />
          <span>{video.views}</span>
        </div>
      </div>
    </div>
  </div>
);

export default VideoPlatform;
