import React, { useState } from 'react';
import { Line, Pie } from 'react-chartjs-2';

function Underwater() {
  const [selectedDate, setSelectedDate] = useState('2020-05-08');
  const [lineChartData, setLineChartData] = useState(null);
  const [pieChartData, setPieChartData] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedMetric, setSelectedMetric] = useState('');

  const styles = {
    // 样式代码保持不变
  };

  const generateFilePath = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `config/软件工程大作业数据/水质数据/${year}-${month}/${year}-${month}-${day}.json`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const filePath = generateFilePath(selectedDate);

      const response = await fetch('http://localhost:3001/visualize-water', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ file_path: filePath, target_column: selectedMetric })
      });

      if (!response.ok) throw new Error(await response.text());

      const data = await response.json();
      if (data.error) throw new Error(data.error);

      setLineChartData(data.line_chart_data);
      setPieChartData(data.pie_chart_data);
    } catch (err) {
      setError(err.message);
      setLineChartData(null);
      setPieChartData(null);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>水下环境监测可视化系统</h1>

      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
          min="2020-05-08"
          max="2021-04-05"
          required
          style={styles.dateInput}
        />
        <select
          value={selectedMetric}
          onChange={(e) => setSelectedMetric(e.target.value)}
          style={{
            padding: '0.8rem',
            borderRadius: '8px',
            border: '2px solid #3498db',
            fontSize: '1.1rem',
            width: '220px',
            outline: 'none',
            transition: 'all 0.3s ease'
          }}
        >
          <option value="">显示全部指标</option>
          <option value="断面名称">断面名称</option>
          <option value="水温(℃)">水温</option>
          <option value="pH(无量纲)">PH值</option>
          <option value="溶解氧(mg/L)">溶解氧</option>
          <option value="电导率(μS/cm)">电导率</option>
          <option value="浊度(NTU)">浊度</option>
          <option value="高锰酸盐指数(mg/L)">高锰酸盐指数</option>
          <option value="氨氮(mg/L)">氨氮</option>
          <option value="总磷(mg/L)">总磷</option>
          <option value="总氮(mg/L)">总氮</option>
          <option value="叶绿素α(mg/L)">叶绿素</option>
          <option value="藻密度(cells/L)">藻密度</option>
        </select>
        <button
          type="submit"
          style={{
            ...styles.submitButton,
            ...(isLoading && styles.submitButton[':disabled'])
          }}
          disabled={isLoading}
        >
          {isLoading ? '数据生成中...' : '开始可视化分析'}
        </button>
      </form>

      {error && <div style={styles.error}>⚠️ {error}</div>}

      {isLoading && (
        <div style={styles.loadingOverlay}>
          <div style={styles.spinner}></div>
          <p style={{ marginTop: '1rem', color: '#3498db' }}>正在分析水质数据...</p>
        </div>
      )}

      <div style={styles.chartContainer}>
        {lineChartData && (
          <div style={styles.chartCard}>
            <h2 style={styles.chartTitle}>水质参数趋势分析</h2>
            <Line
              data={{
                labels: lineChartData.x,
                datasets: [
                  {
                    label: selectedMetric || '全部指标',
                    data: lineChartData.y,
                    borderColor: 'rgba(75, 192, 192, 1)',
                    backgroundColor: 'rgba(75, 192, 192, 0.2)',
                    borderWidth: 1
                  }
                ]
              }}
              options={{
                scales: {
                  x: {
                    title: {
                      display: true,
                      text: '时间'
                    }
                  },
                  y: {
                    title: {
                      display: true,
                      text: '数值'
                    }
                  }
                }
              }}
            />
          </div>
        )}

        {pieChartData && (
          <div style={styles.chartCard}>
            <h2 style={styles.chartTitle}>水质类别分布统计</h2>
            <Pie
              data={{
                labels: pieChartData.labels,
                datasets: [
                  {
                    data: pieChartData.values,
                    backgroundColor: [
                      'rgba(255, 99, 132, 0.2)',
                      'rgba(54, 162, 235, 0.2)',
                      'rgba(255, 205, 86, 0.2)',
                      'rgba(75, 192, 192, 0.2)',
                      'rgba(153, 102, 255, 0.2)'
                    ],
                    borderColor: [
                      'rgba(255, 99, 132, 1)',
                      'rgba(54, 162, 235, 1)',
                      'rgba(255, 205, 86, 1)',
                      'rgba(75, 192, 192, 1)',
                      'rgba(153, 102, 255, 1)'
                    ],
                    borderWidth: 1
                  }
                ]
              }}
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default Underwater;
