import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function Underwater() {
  const [mode, setMode] = useState('local');
  const [loading, setLoading] = useState(false);
  const [dates, setDates] = useState([]);
  const [structure, setStructure] = useState({});
  const [selection, setSelection] = useState({});
  const [charts, setCharts] = useState({});

  // 加载初始数据
  useEffect(() => {
    axios.get('/api/water/dates/').then(res => setDates(res.data.dates));
    axios.get('/api/water/structure/').then(res => setStructure(res.data.structure));
  }, []);

  // 处理本地水质查询
  const handleLocalSearch = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`/api/water/local?date=${selection.date}`);
      setCharts(res.data);
    } finally {
      setLoading(false);
    }
  };

  // 处理地区选择变化
  const handleRegionalSelect = (type, value) => {
    setSelection(prev => ({
      ...prev,
      [type]: value,
      // 清空下级选择
      ...(type === 'province' && { basin: null, location: null }),
      ...(type === 'basin' && { location: null }),
    }));
  };

  // 渲染选择器
  const renderSelector = () => {
    if (mode === 'local') {
      return (
        <div className="selector-group">
          <select 
            value={selection.date || ''}
            onChange={e => setSelection({ date: e.target.value })}
            disabled={!dates.length}
          >
            <option value="">选择日期</option>
            {dates.map(date => (
              <option key={date} value={date}>{date}</option>
            ))}
          </select>
          <button 
            onClick={handleLocalSearch}
            disabled={!selection.date}
          >
            查询
          </button>
        </div>
      );
    }

    return (
      <div className="selector-group">
        <select
          value={selection.province || ''}
          onChange={e => handleRegionalSelect('province', e.target.value)}
        >
          <option value="">选择省份</option>
          {Object.keys(structure).map(p => (
            <option key={p} value={p}>{p}</option>
          ))}
        </select>

        <select
          value={selection.basin || ''}
          onChange={e => handleRegionalSelect('basin', e.target.value)}
          disabled={!selection.province}
        >
          <option value="">选择流域</option>
          {selection.province && Object.keys(structure[selection.province]).map(b => (
            <option key={b} value={b}>{b}</option>
          ))}
        </select>

        <select
          value={selection.location || ''}
          onChange={e => handleRegionalSelect('location', e.target.value)}
          disabled={!selection.basin}
        >
          <option value="">选择地区</option>
          {selection.basin && structure[selection.province][selection.basin].map(l => (
            <option key={l} value={l}>{l}</option>
          ))}
        </select>

        {/* 日期选择... */}
      </div>
    );
  };

  return (
    <div className="underwater-container">
      <div className="mode-selector">
        <button
          className={mode === 'local' ? 'active' : ''}
          onClick={() => setMode('local')}
        >
          本地水质
        </button>
        <button
          className={mode === 'regional' ? 'active' : ''}
          onClick={() => setMode('regional')}
        >
          各地水质
        </button>
      </div>

      {renderSelector()}

      {loading && (
        <div className="loader">
          <div className="spinner"></div>
          <p>数据加载中...</p>
        </div>
      )}

      {charts.line && (
        <div className="chart-container">
          <img src={`data:image/png;base64,${charts.line}`} alt="水质指标变化" />
          {charts.pie && (
            <img src={`data:image/png;base64,${charts.pie}`} alt="水质类别分布" />
          )}
        </div>
      )}
    </div>
  );
}
