import { useState, useEffect } from 'react';
import { Table, Alert, Card, DatePicker, Button, Space, Tag, Modal, Form, Input, message } from 'antd';
import axios from 'axios';
import moment from 'moment';

const { RangePicker } = DatePicker;

function WaterQualityAnomalyDetection() {
  const [isLoading, setIsLoading] = useState(false);
  const [anomalies, setAnomalies] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);
  const [anomalyCount, setAnomalyCount] = useState(0);
  const [thresholds, setThresholds] = useState([]);
  const [thresholdModalVisible, setThresholdModalVisible] = useState(false);
  const [thresholdForm] = Form.useForm();

  const defaultDateRange = [
    moment('2020-05-01T00:00:00'),
    moment('2021-04-05T23:59:59')
  ];

  useEffect(() => {
    fetchThresholds();
    detectAnomalies(defaultDateRange);
  }, []);

  // 获取阈值数据
  const fetchThresholds = async () => {
    try {
      const response = await axios.get('http://localhost:3001/api/water-quality/thresholds');
      setThresholds(response.data);
      
      // 为表单设置初始值
      const formValues = {};
      response.data.forEach(item => {
        formValues[`lower_${item.parameter}`] = item.lower_threshold;
        formValues[`upper_${item.parameter}`] = item.upper_threshold;
      });
      thresholdForm.setFieldsValue(formValues);
    } catch (error) {
      console.error('获取阈值失败:', error);
      message.error('获取阈值失败，请重试');
    }
  };

  // 检测水质数据异常
  const detectAnomalies = async (dateRange) => {
    setIsLoading(true);
    try {
      const startDate = dateRange && dateRange[0] ? dateRange[0].format('YYYY-MM-DD') : '';
      const endDate = dateRange && dateRange[1] ? dateRange[1].format('YYYY-MM-DD') : '';
      
      const response = await axios.get(
        'http://localhost:3001/api/water-quality/detect-anomalies',
        { params: { start_date: startDate, end_date: endDate } }
      );
      
      setAnomalies(response.data.anomalies);
      setTotalRecords(response.data.total_records);
      setAnomalyCount(response.data.anomaly_count);
      
      if (anomalyCount > 0) {
        alert(`检测到 ${anomalyCount} 条异常数据！请查看异常列表。`);
      }
      
    } catch (error) {
      console.error('检测异常失败:', error);
      message.error('检测异常失败，请重试');
    } finally {
      setIsLoading(false);
    }
  };

  // 打开阈值设置模态框
  const openThresholdModal = () => {
    setThresholdModalVisible(true);
  };

  // 关闭阈值设置模态框
  const closeThresholdModal = () => {
    setThresholdModalVisible(false);
  };

  // 保存阈值设置
  const saveThresholds = async () => {
    try {
      const values = await thresholdForm.validateFields();
      
      // 构建请求数据
      const thresholdData = thresholds.map(item => ({
        parameter: item.parameter,
        lower_threshold: parseFloat(values[`lower_${item.parameter}`]),
        upper_threshold: parseFloat(values[`upper_${item.parameter}`])
      }));
      
      // 发送请求更新阈值
      await axios.put('http://localhost:3001/api/water-quality/thresholds', thresholdData);
      
      message.success('阈值设置已保存');
      setThresholdModalVisible(false);
      fetchThresholds(); // 重新加载阈值数据
    } catch (errorInfo) {
      console.error('验证失败:', errorInfo);
      message.error('请检查输入是否正确');
    }
  };

  const columns = [
    {
      title: '记录ID',
      dataIndex: 'record_id',
      key: 'record_id',
      width: 100
    },
    {
      title: '监测站点',
      dataIndex: 'site_id',
      key: 'site_id',
      width: 120
    },
    {
      title: '监测日期',
      dataIndex: 'monitoring_date',
      key: 'monitoring_date',
      width: 140
    },
    {
      title: '监测时间',
      dataIndex: 'monitoring_time',
      key: 'monitoring_time',
      width: 140
    },
    {
      title: '监测断面',
      dataIndex: 'section_name',
      key: 'section_name',
      ellipsis: true,
      width: 200
    },
    {
      title: '水温 (°C)',
      dataIndex: 'parameters.water_temp',
      key: 'water_temp',
      render: (value) => <Tag color="blue">{value}</Tag>
    },
    {
      title: 'pH值',
      dataIndex: 'parameters.ph',
      key: 'ph',
      render: (value) => <Tag color="blue">{value}</Tag>
    },
    {
      title: '溶解氧 (mg/L)',
      dataIndex: 'parameters.dissolved_oxygen',
      key: 'dissolved_oxygen',
      render: (value) => <Tag color="blue">{value}</Tag>
    },
    {
      title: '浊度 (NTU)',
      dataIndex: 'parameters.turbidity',
      key: 'turbidity',
      render: (value) => <Tag color="blue">{value}</Tag>
    },
    {
      title: '氨氮 (mg/L)',
      dataIndex: 'parameters.ammonia_nitrogen',
      key: 'ammonia_nitrogen',
      render: (value) => <Tag color="blue">{value}</Tag>
    },
    {
      title: '异常原因',
      dataIndex: 'anomaly_reasons',
      key: 'anomaly_reasons',
      render: (reasons) => (
        <div className="anomaly-reasons">
          {reasons.map((reason, index) => (
            <Tag color="red" key={index}>{reason}</Tag>
          ))}
        </div>
      )
    }
  ];

  return (
    <div className="water-quality-anomaly-detection p-4">
      <h1 className="text-2xl font-bold mb-6">水质数据异常检测</h1>
      
      <Card title="水质异常检测" className="mb-6">
        <Space>
          <RangePicker 
            showTime 
            format="YYYY-MM-DD HH:mm:ss" 
            placeholder={['开始日期', '结束日期']}
            defaultValue={defaultDateRange}
            onOk={detectAnomalies}
          />
          <Button 
            type="primary" 
            onClick={() => detectAnomalies()}
            loading={isLoading}
            style={{
              backgroundColor: '#1677ff',
              borderColor: '#1677ff',
              color: 'white',
              fontWeight: 'bold',
              borderRadius: '4px',
              padding: '0 20px',
              height: '36px',
              fontSize: '14px',
            }}
          >
            检测异常
          </Button>
          <Button 
            type="secondary" 
            onClick={openThresholdModal}
            style={{
              backgroundColor: '#f0f2f5',
              borderColor: '#d9d9d9',
              color: '#4e5969',
              fontWeight: 'bold',
              borderRadius: '4px',
              padding: '0 20px',
              height: '36px',
              fontSize: '14px',
            }}
          >
            设置阈值
          </Button>
        </Space>
        
        {totalRecords > 0 && (
          <Alert
            message={`检测结果：共${totalRecords}条数据，${anomalyCount}条异常`}
            type={anomalyCount > 0 ? 'warning' : 'success'}
            showIcon
            className="mt-4"
          />
        )}
      </Card>
      
      <Card title="水质异常数据列表">
        {anomalies.length === 0 ? (
          <div className="text-center py-10">
            <span>暂无异常数据</span>
          </div>
        ) : (
          <Table 
            dataSource={anomalies} 
            columns={columns} 
            rowKey="record_id"
            pagination={{ pageSize: 10 }}
            loading={isLoading}
          />
        )}
      </Card>
      
      {/* 阈值设置模态框 */}
      <Modal
        title="水质指标阈值设置"
        visible={thresholdModalVisible}
        onCancel={closeThresholdModal}
        footer={[
          <Button key="cancel" onClick={closeThresholdModal}>
            取消
          </Button>,
          <Button key="save" type="primary" onClick={saveThresholds} loading={isLoading}>
            保存设置
          </Button>,
        ]}
        width={800}
      >
        <Form
          form={thresholdForm}
          layout="vertical"
          initialValues={{ remember: true }}
        >
          <h3 className="mb-4 text-lg font-semibold">水质指标阈值</h3>
          
          {thresholds.map((item, index) => (
            <div key={item.parameter} className="mb-6 pb-6 border-b border-gray-200">
              <h4 className="mb-3 text-md font-medium">{item.parameter}</h4>
              <Form.Item
                label="下限值"
                name={`lower_${item.parameter}`}
                rules={[
                  { required: true, message: '请输入下限值' },
                  { type: 'number', message: '请输入数字' },
                  ({ getFieldValue }) => ({
                    validator(_, value) {
                      const upperValue = getFieldValue(`upper_${item.parameter}`);
                      if (upperValue && value >= upperValue) {
                        return Promise.reject('下限值必须小于上限值');
                      }
                      return Promise.resolve();
                    },
                  }),
                ]}
              >
                <Input type="number" placeholder="请输入下限值" />
              </Form.Item>
              
              <Form.Item
                label="下限值"
                name={`lower_${item.parameter}`}
                rules={[
                    { required: true, message: '请输入下限值' },
                    { type: 'number', message: '请输入数字' },
                    ({ getFieldValue }) => ({
                    validator(_, value) {
                        const upperValue = getFieldValue(`upper_${item.parameter}`);
                        if (upperValue && value >= upperValue) {
                        return Promise.reject('下限值必须小于上限值');
                        }
                        return Promise.resolve();
                    },
                    }),
                ]}
                >
                <InputNumber 
                    placeholder="请输入下限值" 
                    style={{ width: '100%' }}  // 确保与 Input 组件宽度一致
                />
                </Form.Item>
            </div>
          ))}
        </Form>
      </Modal>
    </div>
  );
}

export default WaterQualityAnomalyDetection;
