  // 绑定事件
  function bindEvents() {
    const homePage = document.getElementById('home-page');
    const videoPage = document.getElementById('video-page');
    const videoCards = document.querySelectorAll('.card-hover');
    const videoCover = document.getElementById('video-cover');
    const videoPlayer = document.getElementById('video-player');
    const video = document.getElementById('video');
    const videoSource = document.getElementById('video-source');
    const coverImage = document.getElementById('cover-image');
    const videoTitle = document.getElementById('video-title');
    const videoDescription = document.getElementById('video-description');
    const videoDuration = document.getElementById('video-duration');
    const channelName = document.getElementById('channel-name');
    const channelAvatar = document.getElementById('channel-avatar');
    const viewCount = document.getElementById('view-count');
    const commentCount = document.getElementById('comment-count');
    const backButton = document.querySelector('.text-primary.hover\\:underline');

    // 视频卡片点击事件
    videoCards.forEach(card => {
      card.addEventListener('click', function () {
        const videoId = parseInt(this.getAttribute('data-video-id'));
        const videoData = videos.find(v => v.id === videoId);
        if (videoData) {
          // 填充视频详情
          videoSource.src = videoData.source; // 后端接口路径（如 /api/video）
          coverImage.src = videoData.cover; // 封面路径（本地或后端接口）
          videoTitle.textContent = videoData.title;
          videoDescription.textContent = videoData.description;
          videoDuration.textContent = videoData.duration;
          channelName.textContent = videoData.channel.name;
          channelAvatar.src = videoData.channel.avatar;
          viewCount.textContent = videoData.views + '次观看';
          commentCount.textContent = videoData.comments + '条评论';

          // 加载视频
          video.load();

          // 切换页面
          homePage.classList.add('hidden');
          videoPage.classList.remove('hidden');

          // 滚动到顶部
          window.scrollTo(0, 0);
        }
      });
    });

    // 返回按钮点击事件
    if (backButton) {
      backButton.addEventListener('click', function (e) {
        e.preventDefault();
        video.pause();
        videoPlayer.classList.add('hidden');
        videoCover.classList.remove('hidden');
        homePage.classList.remove('hidden');
        videoPage.classList.add('hidden');
      });
    }

    // 视频播放器功能
    const playPauseBtn = document.getElementById('play-pause');
    const muteBtn = document.getElementById('mute');
    const volumeSlider = document.getElementById('volume');
    const progressBar = document.getElementById('progress-bar');
    const progressContainer = document.getElementById('progress-container');
    const progressHover = document.getElementById('progress-hover');
    const timeDisplay = document.getElementById('time');
    const fullscreenBtn = document.getElementById('fullscreen');
    const pipBtn = document.getElementById('pip');
    const controls = document.getElementById('controls');

    // 点击封面显示播放器并播放视频
    videoCover.addEventListener('click', function () {
      videoCover.classList.add('hidden');
      videoPlayer.classList.remove('hidden');
      video.play();
      updatePlayPauseIcon();
    });

    // 播放/暂停按钮
    playPauseBtn.addEventListener('click', function () {
      if (video.paused) {
        video.play();
      } else {
        video.pause();
      }
      updatePlayPauseIcon();
    });

    // 静音按钮
    muteBtn.addEventListener('click', function () {
      video.muted = !video.muted;
      updateVolumeIcon();
    });

    // 音量控制
    volumeSlider.addEventListener('input', function () {
      video.volume = this.value;
      video.muted = this.value === 0;
      updateVolumeIcon();
    });

    // 更新播放/暂停图标
    function updatePlayPauseIcon() {
      if (video.paused) {
        playPauseBtn.innerHTML = '<i class="fa fa-play text-xl"></i>';
      } else {
        playPauseBtn.innerHTML = '<i class="fa fa-pause text-xl"></i>';
      }
    }

    // 更新音量图标
    function updateVolumeIcon() {
      if (video.muted || video.volume === 0) {
        muteBtn.innerHTML = '<i class="fa fa-volume-off text-xl"></i>';
        volumeSlider.value = 0;
      } else if (video.volume < 0.5) {
        muteBtn.innerHTML = '<i class="fa fa-volume-down text-xl"></i>';
      } else {
        muteBtn.innerHTML = '<i class="fa fa-volume-up text-xl"></i>';
      }
    }

    // 更新进度条
    video.addEventListener('timeupdate', function () {
      const percent = video.currentTime / video.duration * 100;
      progressBar.style.width = percent + '%';
      updateTimeDisplay();
    });

    // 更新时间显示
    function updateTimeDisplay() {
      const currentMinutes = Math.floor(video.currentTime / 60);
      const currentSeconds = Math.floor(video.currentTime % 60);
      const durationMinutes = Math.floor(video.duration / 60);
      const durationSeconds = Math.floor(video.duration % 60);
      timeDisplay.textContent = `${currentMinutes}:${currentSeconds.toString().padStart(2, '0')} / ${durationMinutes}:${durationSeconds.toString().padStart(2, '0')}`;
    }

    // 点击进度条跳转到指定位置
    progressContainer.addEventListener('click', function (e) {
      const rect = this.getBoundingClientRect();
      const pos = (e.clientX - rect.left) / rect.width;
      video.currentTime = pos * video.duration;
    });

    // 显示进度条悬停预览
    progressContainer.addEventListener('mousemove', function (e) {
      const rect = this.getBoundingClientRect();
      const pos = (e.clientX - rect.left) / rect.width;
      progressHover.style.width = pos * 100 + '%';
      progressHover.classList.remove('hidden');

      // 计算预览时间
      const previewTime = pos * video.duration;
      const previewMinutes = Math.floor(previewTime / 60);
      const previewSeconds = Math.floor(previewTime % 60);

      // 创建预览时间提示
      let previewTooltip = document.getElementById('preview-tooltip');
      if (!previewTooltip) {
        previewTooltip = document.createElement('div');
        previewTooltip.id = 'preview-tooltip';
        previewTooltip.className = 'absolute -top-8 bg-black/80 text-white text-xs py-1 px-2 rounded pointer-events-none';
        this.appendChild(previewTooltip);
      }
      previewTooltip.textContent = `${previewMinutes}:${previewSeconds.toString().padStart(2, '0')}`;
      previewTooltip.style.left = e.clientX - rect.left - 10 + 'px';
    });
    progressContainer.addEventListener('mouseleave', function () {
      progressHover.classList.add('hidden');
      const previewTooltip = document.getElementById('preview-tooltip');
      if (previewTooltip) {
        previewTooltip.remove();
      }
    });

    // 全屏按钮
    fullscreenBtn.addEventListener('click', function () {
      if (!document.fullscreenElement) {
        video.requestFullscreen().catch(err => {
          console.log(`全屏请求错误: ${err.message}`);
        });
        fullscreenBtn.innerHTML = '<i class="fa fa-compress text-xl"></i>';
      } else {
        document.exitFullscreen();
        fullscreenBtn.innerHTML = '<i class="fa fa-expand text-xl"></i>';
      }
    });

    // 监听全屏状态变化
    document.addEventListener('fullscreenchange', function () {
      if (document.fullscreenElement) {
        fullscreenBtn.innerHTML = '<i class="fa fa-compress text-xl"></i>';
      } else {
        fullscreenBtn.innerHTML = '<i class="fa fa-expand text-xl"></i>';
      }
    });

    // 画中画按钮
    pipBtn.addEventListener('click', function () {
      if (document.pictureInPictureElement) {
        document.exitPictureInPicture();
      } else if (document.pictureInPictureEnabled) {
        video.requestPictureInPicture();
      }
    });

    // 隐藏/显示控制条
    let controlsTimeout;
    video.addEventListener('mousemove', function () {
      controls.style.opacity = '1';
      clearTimeout(controlsTimeout);
      controlsTimeout = setTimeout(function () {
        if (!video.paused) {
          controls.style.opacity = '0';
        }
      }, 3000);
    });

    // 键盘控制
    document.addEventListener('keydown', function (e) {
      switch (e.key) {
        case ' ':
        case 'k':
          if (videoPlayer.classList.contains('hidden')) {
            videoCover.click();
          } else {
            playPauseBtn.click();
          }
          e.preventDefault();
          break;
        case 'f':
          fullscreenBtn.click();
          e.preventDefault();
          break;
        case 'm':
          muteBtn.click();
          e.preventDefault();
          break;
        case 'ArrowRight':
          video.currentTime += 10;
          e.preventDefault();
          break;
        case 'ArrowLeft':
          video.currentTime -= 10;
          e.preventDefault();
          break;
        case 'ArrowUp':
          volumeSlider.value = Math.min(1, parseFloat(volumeSlider.value) + 0.1);
          volumeSlider.dispatchEvent(new Event('input'));
          e.preventDefault();
          break;
        case 'ArrowDown':
          volumeSlider.value = Math.max(0, parseFloat(volumeSlider.value) - 0.1);
          volumeSlider.dispatchEvent(new Event('input'));
          e.preventDefault();
          break;
      }
    });

    // 视频结束时重置
    video.addEventListener('ended', function () {
      playPauseBtn.innerHTML = '<i class="fa fa-play text-xl"></i>';
    });

    // 导航栏滚动效果
    const nav = document.querySelector('nav');
    let lastScrollTop = 0;
    window.addEventListener('scroll', function () {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      if (scrollTop > 100) {
        nav.classList.add('py-2');
        nav.classList.remove('py-3');
      } else {
        nav.classList.add('py-3');
        nav.classList.remove('py-2');
      }
      lastScrollTop = scrollTop;
    });
  }

  // 初始化应用
  createApp();
  bindEvents();
});

