// src/pages/Video.js
import React, { useState, useEffect, useRef } from 'react';

// 视频播放器应用
document.addEventListener('DOMContentLoaded', function() {
  // 配置 Tailwind 自定义主题
  tailwind.config = {
    theme: {
      extend: {
        colors: {
          primary: '#FB7299',
          secondary: '#23ADE5',
          dark: '#18191C',
          light: '#F4F4F4',
        },
        fontFamily: {
          sans: ['Inter', 'system-ui', 'sans-serif'],
        },
      }
    }
  }

  // 自定义工具类
  const customStyles = document.createElement('style');
  //customStyles.setAttribute('type', 'text/tailwindcss');
  customStyles.textContent = `
    @layer utilities {
      .content-auto {
        content-visibility: auto;
      }
      .video-container {
        aspect-ratio: 16/9;
      }
      .play-btn {
        transform: translate(-50%, -50%);
      }
      .progress-bar {
        height: 4px;
        transform-origin: left;
      }
      .volume-slider {
        -webkit-appearance: none;
        height: 4px;
      }
      .volume-slider::-webkit-slider-thumb {
        -webkit-appearance: none;
        width: 12px;
        height: 12px;
        border-radius: 50%;
        background: white;
        cursor: pointer;
      }
      .card-hover {
        transition: all 0.3s ease;
      }
      .card-hover:hover {
        transform: translateY(-5px);
      }
    }
  `;
  document.head.appendChild(customStyles);

  // 模拟视频数据
  const videos = [
    {
      id: 1,
      title: "这是一个海洋鱼类视频",
      description: "这是视频的简介内容，包含了关于这个视频的详细描述和相关信息。",
      //改
      cover: "https://picsum.photos/1280/720?random=2",
      duration: "00:20",
      source: "/api/video",
      channel: {
        name: "UP主名称",
        avatar: "https://picsum.photos/32/32?random=1"
      },
      views: "10.2万",
      comments: "500"
    },
    {
      id: 2,
      title: "这个视频展示了一种非常有趣的技术实现方法",
      description: "这是一个关于技术实现的视频，详细讲解了如何使用最新的技术来解决实际问题，非常适合技术爱好者观看和学习。",
      cover: "https://picsum.photos/1280/720?random=2",
      duration: "08:45",
      source: "https://samplelib.com/lib/preview/mp4/sample-10s.mp4",
      channel: {
        name: "技术UP主",
        avatar: "https://picsum.photos/32/32?random=2"
      },
      views: "7.8万",
      comments: "320"
    },
    // 其他视频数据...
  ];


  const VideoPage = () => {
    const [currentVideo, setCurrentVideo] = useState(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [volume, setVolume] = useState(1);
    const [progress, setProgress] = useState(0);
    const [isMuted, setIsMuted] = useState(false);
    const [isFullscreen, setIsFullscreen] = useState(false);
    const videoRef = useRef(null);
    const controlsRef = useRef(null);
    const navRef = useRef(null);
  
    // 视频控制逻辑
    const togglePlayPause = () => {
      if (videoRef.current.paused) {
        videoRef.current.play();
      } else {
        videoRef.current.pause();
      }
      setIsPlaying(!videoRef.current.paused);
    };
  
    const handleVolumeChange = (e) => {
      const newVolume = parseFloat(e.target.value);
      setVolume(newVolume);
      videoRef.current.volume = newVolume;
      setIsMuted(newVolume === 0);
    };
  
    // 进度条更新
    const updateProgress = () => {
      const progress = (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(progress);
    };
  
    // 全屏切换
    const toggleFullscreen = () => {
      if (!document.fullscreenElement) {
        videoRef.current.requestFullscreen();
        setIsFullscreen(true);
      } else {
        document.exitFullscreen();
        setIsFullscreen(false);
      }
    };
  
    // 事件监听
    useEffect(() => {
      const handleScroll = () => {
        if (window.scrollY > 100) {
          navRef.current.classList.add('py-2');
          navRef.current.classList.remove('py-3');
        } else {
          navRef.current.classList.add('py-3');
          navRef.current.classList.remove('py-2');
        }
      };
  
      window.addEventListener('scroll', handleScroll);
      return () => window.removeEventListener('scroll', handleScroll);
    }, []);
  
    return (
      <div className="bg-gray-100 font-sans text-dark min-h-screen">
        {/* 导航栏组件 */}
        <nav ref={navRef} className="fixed top-0 left-0 right-0 bg-white shadow-md z-50 transition-all duration-300">
          {/* 导航栏内容 */}
        </nav>
  
        <main className="container mx-auto px-4 pt-24 pb-12">
          {!currentVideo ? (
            <HomePage videos={videos} onVideoSelect={setCurrentVideo} />
          ) : (
            <VideoPlayer 
              video={currentVideo}
              isPlaying={isPlaying}
              volume={volume}
              progress={progress}
              isMuted={isMuted}
              isFullscreen={isFullscreen}
              onPlayPause={togglePlayPause}
              onVolumeChange={handleVolumeChange}
              onFullscreen={toggleFullscreen}
              videoRef={videoRef}
            />
          )}
        </main>
  
        <Footer />
      </div>
    );
  };
  
  // 首页组件
  const HomePage = ({ videos, onVideoSelect }) => (
    <div id="home-page">
      <Carousel />
      <CategoryNav />
      <VideoList videos={videos} onSelect={onVideoSelect} />
    </div>
  );
  
  // 视频卡片组件
  const VideoCard = ({ video, onSelect }) => (
    <div 
      className="bg-white rounded-xl shadow-md overflow-hidden card-hover"
      onClick={() => onSelect(video)}
    >
      {/* 视频卡片内容 */}
    </div>
  );
  
  // 视频播放器组件
  const VideoPlayer = ({ video, ...props }) => (
    <div className="flex flex-col md:flex-row gap-6">
      <div className="w-full md:w-2/3">
        <div className="bg-white rounded-xl shadow-lg overflow-hidden transition-all hover:shadow-xl">
          <video 
            ref={props.videoRef}
            className="w-full h-full object-contain"
            onTimeUpdate={updateProgress}
            // 其他视频属性
          >
            <source src={video.source} type="video/mp4" />
          </video>
          
          {/* 视频控制条 */}
          <div ref={controlsRef} className="controls">
            {/* 控制条内容 */}
          </div>
        </div>
      </div>
    </div>
  );

