import React, { useState, useEffect, useRef } from 'react';

const VideoPlatform = () => {
  // 状态管理
  const [videos, setVideos] = useState([]);
  const [currentVideo, setCurrentVideo] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [progress, setProgress] = useState(0);
  const [showControls, setShowControls] = useState(true);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [isMuted, setIsMuted] = useState(false);

  // 引用
  const videoRef = useRef(null);
  const progressRef = useRef(null);
  const containerRef = useRef(null);

  // 模拟视频数据
  useEffect(() => {
    const fetchVideos = async () => {
      // 实际开发中替换为API调用
      const mockData = [
        {
          id: 1,
          title: "海洋鱼类探索",
          description: "深入海底世界，探索神秘的海洋生物",
          cover: "https://picsum.photos/1280/720?random=1",
          duration: "00:20",
          source: "/api/video",
          channel: {
            name: "海洋频道",
            avatar: "https://picsum.photos/32/32?random=1"
          },
          views: "10.2万",
          comments: "500"
        },
        {
          id: 2,
          title: "技术实现方法详解",
          description: "最新技术实现方法深度解析",
          cover: "https://picsum.photos/1280/720?random=2",
          duration: "08:45",
          source: "https://samplelib.com/lib/preview/mp4/sample-10s.mp4",
          channel: {
            name: "技术UP主",
            avatar: "https://picsum.photos/32/32?random=2"
          },
          views: "7.8万",
          comments: "320"
        }
      ];
      setVideos(mockData);
    };

    fetchVideos();
  }, []);

  // 视频控制逻辑
  const handlePlayPause = () => {
    if (videoRef.current.paused) {
      videoRef.current.play();
      setIsPlaying(true);
    } else {
      videoRef.current.pause();
      setIsPlaying(false);
    }
  };

  const handleMute = () => {
    videoRef.current.muted = !isMuted;
    setIsMuted(!isMuted);
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    videoRef.current.volume = newVolume;
  };

  const handleTimeUpdate = () => {
    setProgress(
      (videoRef.current.currentTime / videoRef.current.duration) * 100
    );
  };

  const handleProgressClick = (e) => {
    const rect = progressRef.current.getBoundingClientRect();
    const pos = (e.clientX - rect.left) / rect.width;
    videoRef.current.currentTime = pos * videoRef.current.duration;
  };

  const handleFullscreen = () => {
    if (!document.fullscreenElement) {
      containerRef.current.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  // 格式化时间显示
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = Math.floor(seconds % 60);
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };





  return (
    <div className="bg-gray-100 min-h-screen font-sans">
      {/* 导航栏 */}
      <nav className="fixed top-0 left-0 right-0 bg-white shadow-md z-50 transition-all duration-300">
        <div className="container mx-auto px-4 py-3 flex items-center justify-between">
          {/* 导航内容保持不变 */}
        </div>
      </nav>

      {/* 主内容区 */}
      <main className="container mx-auto px-4 pt-24 pb-12">
        {/* 视频列表 */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {videos.map(video => (
            <VideoCard
              key={video.id}
              video={video}
              onClick={() => setCurrentVideo(video)}
            />
          ))}
        </div>

        {/* 视频播放页 */}
        {currentVideo && (
          <div className="fixed top-0 left-0 right-0 bottom-0 bg-black z-50">
            <div className="relative h-full max-w-7xl mx-auto">
              <button
                onClick={() => setCurrentVideo(null)}
                className="absolute top-4 left-4 text-white z-10"
              >
                ← 返回
              </button>

              <div className="video-container aspect-video mt-16">
                <video
                  ref={videoRef}
                  src={currentVideo.source}
                  poster={currentVideo.cover}
                  className="w-full h-full object-cover"
                  onTimeUpdate={handleTimeUpdate}
                />

                {/* 控制条 */}
                {showControls && (
                  <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
                    {/* 进度条 */}
                    <div
                      ref={progressRef}
                      className="w-full h-1 bg-gray-600 rounded-full cursor-pointer mb-3"
                      onClick={handleProgressClick}
                    >
                      <div
                        className="h-full bg-primary rounded-full"
                        style={{ width: `${progress}%` }}
                      />
                    </div>

                    {/* 控制按钮 */}
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-4">
                        <button onClick={handlePlayPause}>
                          {isPlaying ? (
                            <i className="fa fa-pause text-white text-xl" />
                          ) : (
                            <i className="fa fa-play text-white text-xl" />
                          )}
                        </button>

                        <div className="flex items-center">
                          <button onClick={handleMute}>
                            {isMuted ? (
                              <i className="fa fa-volume-off text-white text-xl" />
                            ) : volume > 0.5 ? (
                              <i className="fa fa-volume-up text-white text-xl" />
                            ) : (
                              <i className="fa fa-volume-down text-white text-xl" />
                            )}
                          </button>
                          <input
                            type="range"
                            min="0"
                            max="1"
                            step="0.05"
                            value={volume}
                            onChange={handleVolumeChange}
                            className="volume-slider ml-2"
                          />
                        </div>

                        <div className="text-white text-sm">
                          {formatTime(videoRef.current?.currentTime || 0)} / 
                          {formatTime(videoRef.current?.duration || 0)}
                        </div>
                      </div>

                      <div className="flex items-center space-x-4">
                        <button onClick={handleFullscreen}>
                          <i className="fa fa-expand text-white text-xl" />
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* 视频信息 */}
              <div className="p-6 text-white">
                <h1 className="text-2xl font-bold mb-3">{currentVideo.title}</h1>
                {/* 其他视频信息保持不变 */}
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

// 视频卡片组件
const VideoCard = ({ video, onClick }) => (
  <div className="bg-white rounded-xl shadow-md overflow-hidden card-hover cursor-pointer">
    <img src={video.cover} alt={video.title} className="w-full aspect-video object-cover" />
    <div className="p-4">
      <h3 className="font-medium line-clamp-2 mb-2">{video.title}</h3>
      <div className="flex items-center justify-between">
        <div className="flex items-center">
          <img src={video.channel.avatar} alt="UP主头像" className="w-6 h-6 rounded-full mr-2" />
          <span className="text-sm text-gray-600 truncate">{video.channel.name}</span>
        </div>
        <div className="flex items-center text-sm text-gray-600">
          <i className="fa fa-eye mr-1" />
          <span>{video.views}</span>
        </div>
      </div>
    </div>
  </div>
);

export default VideoPlatform;
