import React, { useState, useEffect, useRef } from 'react';
import { Card, Row, Col, Select, Spin, message, Button, Space } from 'antd';
import { Bar, Scatter } from 'react-chartjs-2';
import Chart from 'chart.js/auto';
import { DownloadOutlined } from '@ant-design/icons';

// 导入依赖
import html2canvas from 'html2canvas';
import 'jspdf';

const { Option } = Select;

function FishDataVisualization() {
  const [fishData, setFishData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedSpecies, setSelectedSpecies] = useState('');
  const [availableSpecies, setAvailableSpecies] = useState([]);
  const [isDownloading, setIsDownloading] = useState(false);
  
  // 图表引用
  const weightBarChartRef = useRef(null);
  const generalScatterChartRef = useRef(null);
  const singleSpeciesChartRef = useRef(null);

  // 获取鱼类数据
  useEffect(() => {
    fetchFishData();
  }, [selectedSpecies]);

  const fetchFishData = async () => {
    setIsLoading(true);
    try {
      const formData = new FormData();
      if (selectedSpecies) {
        formData.append('species', selectedSpecies);
      }

      const response = await fetch('http://localhost:3001/visualize-fish', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) throw new Error(await response.text());

      const data = await response.json();
      setFishData(data);

      // 提取鱼种列表
      if (data.bar_chart_data) {
        setAvailableSpecies(data.bar_chart_data.map(item => item.Species));
      }

    } catch (err) {
      console.error('获取数据失败:', err);
      message.error(err.message || '获取数据失败，请检查网络');
    } finally {
      setIsLoading(false);
    }
  };

  // 渲染柱状图
  const renderWeightBarChart = () => {
    if (!fishData || !fishData.bar_chart_data) return null;

    const { bar_chart_data } = fishData;
    const labels = bar_chart_data.map(item => item.Species);
    const values = bar_chart_data.map(item => item['Weight(g)']);

    return (
      <Card 
        title={
          <div className="flex justify-between items-center">
            <span>每种鱼类的平均重量</span>
            <Space>
              <Button 
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => {
                  console.log('按钮被点击');
                  downloadChart(weightBarChartRef, '平均重量柱状图');
                }}
              >
                PNG
              </Button>
              <Button 
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => downloadChartAsPDF([weightBarChartRef], '鱼类重量分析报告')}
                loading={isDownloading}
              >
                PDF
              </Button>
            </Space>
          </div>
        }
        bordered={true}
      >
        <div style={{ height: '350px' }}>
          <Bar
            ref={weightBarChartRef}
            data={{
              labels: labels,
              datasets: [{
                label: '平均重量 (g)',
                data: values,
                backgroundColor: [
                  'rgba(54, 162, 235, 0.7)',
                  'rgba(255, 99, 132, 0.7)',
                  'rgba(255, 206, 86, 0.7)',
                  'rgba(75, 192, 192, 0.7)',
                  'rgba(153, 102, 255, 0.7)',
                  'rgba(255, 159, 64, 0.7)',
                  'rgba(173, 216, 230, 0.7)'
                ],
                borderColor: [
                  'rgb(54, 162, 235)',
                  'rgb(255, 99, 132)',
                  'rgb(255, 206, 86)',
                  'rgb(75, 192, 192)',
                  'rgb(153, 102, 255)',
                  'rgb(255, 159, 64)',
                  'rgb(173, 216, 230)'
                ],
                borderWidth: 1
              }]
            }}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
                legend: { position: 'top' }
              },
              scales: { y: { beginAtZero: true, title: { text: '平均重量 (g)' } } }
            }}
          />
        </div>
      </Card>
    );
  };

  // 渲染散点图
  const renderGeneralScatterChart = () => {
    if (!fishData || !fishData.scatter_chart_data) return null;

    const speciesGroups = {};
    fishData.scatter_chart_data.forEach(item => {
      if (!speciesGroups[item.Species]) speciesGroups[item.Species] = [];
      speciesGroups[item.Species].push({ x: item['Length1(cm)'], y: item['Width(cm)'] });
    });

    const datasets = Object.keys(speciesGroups).map(species => ({
      label: species,
      data: speciesGroups[species],
      backgroundColor: getRandomColor(0.7),
      borderColor: getRandomColor(),
      borderWidth: 1,
      pointRadius: 4,
      pointHoverRadius: 6
    }));

    return (
      <Card 
        title={
          <div className="flex justify-between items-center">
            <span>鱼类的长度和宽度关系</span>
            <Space>
              <Button 
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => downloadChart(generalScatterChartRef, '长度宽度散点图')}
                loading={isDownloading}
              >
                PNG
              </Button>
              <Button 
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => downloadChartAsPDF([generalScatterChartRef], '鱼类尺寸关系报告')}
                loading={isDownloading}
              >
                PDF
              </Button>
            </Space>
          </div>
        }
        bordered={true}
      >
        <div style={{ height: '350px' }}>
          <Scatter
            ref={generalScatterChartRef}
            data={{ datasets }}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: { legend: { position: 'top' } },
              scales: {
                x: { type: 'linear', position: 'bottom', title: { text: '长度 (cm)' } },
                y: { title: { text: '宽度 (cm)' } }
              }
            }}
          />
        </div>
      </Card>
    );
  };

  // 渲染特定鱼种散点图
  const renderSingleSpeciesScatterChart = () => {
    if (!fishData || !fishData.single_species_data) return null;

    const { single_species_data } = fishData;
    const { species, data, stats } = single_species_data;
    const scatterData = data.map(item => ({ x: item['Length1(cm)'], y: item['Width(cm)'] }));
    const regressionLine = calculateRegressionLine(scatterData);

    return (
      <Card
        title={
          <div className="flex justify-between items-center">
            <span>{`${species} 的长度和宽度关系 (相关系数: ${stats.correlation.toFixed(2)})`}</span>
            <Space>
              <Button 
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => downloadChart(singleSpeciesChartRef, `${species}尺寸关系图`)}
                loading={isDownloading}
              >
                PNG
              </Button>
              <Button 
                icon={<DownloadOutlined />} 
                size="small"
                onClick={() => downloadChartAsPDF([singleSpeciesChartRef], `${species}数据分析报告`)}
                loading={isDownloading}
              >
                PDF
              </Button>
            </Space>
          </div>
        }
        borderColor="#1890ff"
        style={{ borderWidth: 2 }}
      >
        <div style={{ height: '350px' }}>
          <Scatter
            ref={singleSpeciesChartRef}
            data={{
              datasets: [
                {
                  label: `${species} 样本点`,
                  data: scatterData,
                  backgroundColor: 'rgba(30, 144, 255, 0.5)',
                  borderColor: 'rgba(30, 144, 255, 1)',
                  borderWidth: 1,
                  pointRadius: 4,
                  pointHoverRadius: 6
                },
                {
                  label: '回归线',
                  data: regressionLine.data,
                  backgroundColor: 'rgba(220, 20, 60, 0)',
                  borderColor: 'rgba(220, 20, 60, 1)',
                  borderWidth: 2,
                  pointRadius: 0,
                  showLine: true,
                  fill: false
                }
              ]
            }}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: { legend: { position: 'top' } },
              scales: {
                x: { type: 'linear', position: 'bottom', title: { text: '长度 (cm)' } },
                y: { title: { text: '宽度 (cm)' } }
              }
            }}
          />
        </div>
        <div style={{ padding: '10px', backgroundColor: '#f5f7fa', borderRadius: '4px', marginTop: '10px' }}>
          <p>样本数量: {stats.sample_size} | 平均长度: {stats.avg_length.toFixed(2)} cm | 平均宽度: {stats.avg_width.toFixed(2)} cm</p>
        </div>
      </Card>
    );
  };

  // 计算回归线
  const calculateRegressionLine = (dataPoints) => {
    const xValues = dataPoints.map(p => p.x);
    const yValues = dataPoints.map(p => p.y);
    const n = xValues.length;
    let sumX = 0, sumY = 0, sumXY = 0, sumX2 = 0;

    for (let i = 0; i < n; i++) {
      sumX += xValues[i];
      sumY += yValues[i];
      sumXY += xValues[i] * yValues[i];
      sumX2 += xValues[i] * xValues[i];
    }

    const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
    const intercept = (sumY - slope * sumX) / n;

    const xMin = Math.min(...xValues);
    const xMax = Math.max(...xValues);
    const regressionPoints = [
      { x: xMin, y: slope * xMin + intercept },
      { x: xMax, y: slope * xMax + intercept }
    ];

    return { data: regressionPoints, slope, intercept };
  };

  // 生成随机颜色
  const getRandomColor = (alpha = 1) => {
    const r = Math.floor(Math.random() * 256);
    const g = Math.floor(Math.random() * 256);
    const b = Math.floor(Math.random() * 256);
    return `rgba(${r}, ${g}, ${b}, ${alpha})`;
  };

  // 下载PNG
  const downloadChart = (chartRef, fileName) => {
    if (!chartRef.current || !chartRef.current.chartInstance) {
      message.error('图表尚未加载完成，请稍候再试');
      return;
    }

    setIsDownloading(true);
    try {
      const canvas = chartRef.current.chartInstance.canvas;
      if (!canvas) throw new Error('无法获取图表Canvas');
      
      const imageData = canvas.toDataURL('image/png');
      const link = document.createElement('a');
      link.download = `${fileName}.png`;
      link.href = imageData;
      link.click();
      
      message.success('图表下载成功');
    } catch (error) {
      console.error('PNG下载失败:', error);
      message.error(`下载失败: ${error.message}`);
    } finally {
      setIsDownloading(false);
    }
  };

  // 下载PDF
  const downloadChartAsPDF = async (chartRefs, fileName) => {
    if (!window.jspdf) {
      message.warning('请先安装jsPDF库以使用PDF下载功能');
      return;
    }

    setIsDownloading(true);
    try {
      const { jsPDF } = window.jspdf;
      const doc = new jsPDF('landscape');
      
      await Promise.all(chartRefs.map(async (chartRef, index) => {
        if (chartRef.current && chartRef.current.chartInstance) {
          const canvas = chartRef.current.chartInstance.canvas;
          const imgData = await html2canvas(canvas, { scale: 2, useCORS: true });
          
          if (index > 0) doc.addPage();
          const imgWidth = 280;
          const imgHeight = (canvas.height * imgWidth) / canvas.width;
          doc.addImage(imgData.toDataURL('image/jpeg'), 'JPEG', 10, 10, imgWidth, imgHeight);
        }
      }));
      
      doc.save(`${fileName}.pdf`);
      message.success('PDF下载成功');
    } catch (error) {
      console.error('PDF下载失败:', error);
      message.error(`下载失败: ${error.message}`);
    } finally {
      setIsDownloading(false);
    }
  };

  // 下载所有图表
  const downloadAllCharts = () => {
    const charts = [];
    if (weightBarChartRef.current) charts.push(weightBarChartRef);
    if (generalScatterChartRef.current) charts.push(generalScatterChartRef);
    if (singleSpeciesChartRef.current) charts.push(singleSpeciesChartRef);
    
    if (charts.length === 0) {
      message.error('没有可下载的图表');
      return;
    }
    
    downloadChartAsPDF(charts, '鱼类数据可视化综合报告');
  };

  // 处理鱼种选择
  const handleSpeciesChange = (value) => {
    setSelectedSpecies(value);
  };

  return (
    <div style={{ padding: '20px' }}>
      <div className="flex justify-between items-center mb-6">
        <h1 style={{ color: '#1890ff', fontSize: '2rem', fontWeight: '600' }}>
          鱼类数据可视化分析
        </h1>
        <Button 
          type="primary" 
          icon={<DownloadOutlined />} 
          onClick={downloadAllCharts}
          loading={isDownloading || isLoading}
        >
          下载全部图表
        </Button>
      </div>

      <Spin spinning={isLoading} tip="加载鱼类数据...">
        <div style={{ marginBottom: '30px', textAlign: 'center' }}>
          <Select
            value={selectedSpecies}
            onChange={handleSpeciesChange}
            style={{ width: '300px', height: '52px', fontSize: '16px' }}
            placeholder="选择鱼种查看详细分析"
          >
            <Option value="">全部鱼类</Option>
            {availableSpecies.map((species, index) => (
              <Option key={index} value={species}>{species}</Option>
            ))}
          </Select>
        </div>

        <Row gutter={16}>
          <Col xs={24} md={12}>
            {renderWeightBarChart()}
          </Col>
          <Col xs={24} md={12}>
            {renderGeneralScatterChart()}
          </Col>
        </Row>

        {selectedSpecies && renderSingleSpeciesScatterChart()}
      </Spin>
    </div>
  );
}

export default FishDataVisualization;
