// src/pages/Underwater/Underwater.js
import React, { useState, useEffect } from 'react';
import { Select, DatePicker, Cascader, Spin, Card, Tabs } from 'antd';
import axios from 'axios';
import styled from 'styled-components';

const { TabPane } = Tabs;
const { Option } = Select;

const Container = styled.div`
  padding: 24px;
  max-width: 1200px;
  margin: 0 auto;
`;

const ChartContainer = styled.div`
  margin-top: 32px;
  background: white;
  padding: 24px;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.05);
`;

const WaterQualityDashboard = () => {
  const [mode, setMode] = useState('local');
  const [loading, setLoading] = useState(false);
  const [dates, setDates] = useState([]);
  const [provinces, setProvinces] = useState([]);
  const [charts, setCharts] = useState({
    lineChart: null,
    pieChart: null
  });

  // 初始化数据
  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        // 获取本地可用日期
        const datesRes = await axios.get('/api/water/local/dates');
        setDates(datesRes.data);

        // 获取省份列表
        const provincesRes = await axios.get('/api/water/regions/provinces');
        setProvinces(provincesRes.data.map(p => ({
          value: p,
          label: p,
          isLeaf: false
        })));
      } catch (error) {
        console.error('初始化数据获取失败:', error);
      }
    };

    fetchInitialData();
  }, []);

  // 处理本地日期选择
  const handleLocalDateChange = async (dateString) => {
    if (!dateString) return;

    setLoading(true);
    try {
      const res = await axios.get('/api/water/visualize', {
        params: { type: 'local', date: dateString }
      });
      setCharts(res.data);
    } catch (error) {
      console.error('数据获取失败:', error);
    } finally {
      setLoading(false);
    }
  };

  // 处理地域层级选择
  const handleRegionSelect = async (value, selectedOptions) => {
    if (value.length < 3) return;

    const [province, basin, area] = value;
    setLoading(true);

    try {
      const res = await axios.get('/api/water/visualize', {
        params: {
          type: 'regional',
          province,
          basin,
          area,
          date: '2021-04'  // 固定四月数据
        }
      });
      setCharts(res.data);
    } catch (error) {
      console.error('地域数据获取失败:', error);
    } finally {
      setLoading(false);
    }
  };

  // 动态加载级联选项
  const loadRegionData = async (selectedOptions) => {
    const targetOption = selectedOptions[selectedOptions.length - 1];

    try {
      let newOptions = [];
      if (selectedOptions.length === 1) {  // 加载流域
        const res = await axios.get('/api/water/regions/basins', {
          params: { province: targetOption.value }
        });
        newOptions = res.data.map(b => ({
          value: b,
          label: b,
          isLeaf: false
        }));
      } else if (selectedOptions.length === 2) {  // 加载地区
        const res = await axios.get('/api/water/regions/areas', {
          params: {
            province: selectedOptions[0].value,
            basin: targetOption.value
          }
        });
        newOptions = res.data.map(a => ({
          value: a,
          label: a,
          isLeaf: true
        }));
      }

      targetOption.children = newOptions;
      setProvinces([...provinces]);
    } catch (error) {
      console.error('地域选项加载失败:', error);
    }
  };

  return (
    <Container>
      <h1 style={{ marginBottom: 32 }}>水下环境监测分析系统</h1>

      <Card bordered={false}>
        <Tabs
          activeKey={mode}
          onChange={setMode}
          tabBarStyle={{ marginBottom: 24 }}
        >
          <TabPane tab="本地水质分析" key="local">
            <div style={{ display: 'flex', gap: 16, alignItems: 'center' }}>
              <span>选择监测日期：</span>
              <DatePicker
                style={{ width: 240 }}
                disabledDate={current => 
                  !dates.includes(current.format('YYYY-MM-DD'))
                }
                onChange={(_, dateString) => handleLocalDateChange(dateString)}
                placeholder="请选择日期"
                allowClear={false}
              />
            </div>
          </TabPane>

          <TabPane tab="地域水质分析" key="regional">
            <div style={{ display: 'flex', gap: 16, alignItems: 'center' }}>
              <span>选择监测区域：</span>
              <Cascader
                style={{ width: 400 }}
                options={provinces}
                loadData={loadRegionData}
                onChange={handleRegionSelect}
                changeOnSelect={false}
                placeholder="请选择省份/流域/监测点"
              />
            </div>
          </TabPane>
        </Tabs>

        <ChartContainer>
          {loading ? (
            <div style={{ textAlign: 'center', padding: 40 }}>
              <Spin size="large" tip="数据加载中..." />
            </div>
          ) : (
            <>
              {charts.lineChart && (
                <img 
                  src={`data:image/png;base64,${charts.lineChart}`}
                  alt="水质指标变化趋势"
                  style={{ width: '100%', marginBottom: 32 }}
                />
              )}

              {charts.pieChart && (
                <div style={{ maxWidth: 600, margin: '0 auto' }}>
                  <img
                    src={`data:image/png;base64,${charts.pieChart}`}
                    alt="水质类别分布"
                    style={{ width: '100%' }}
                  />
                </div>
              )}

              {!charts.lineChart && !loading && (
                <div style={{ textAlign: 'center', color: 'rgba(0, 0, 0, 0.45)', padding: 40 }}>
                  请选择查询条件以显示分析结果
                </div>
              )}
            </>
          )}
        </ChartContainer>
      </Card>
    </Container>
  );
};

export default WaterQualityDashboard;
