import React, { useState } from 'react';
import { Row, Col, Card, Select, Button, Spin, message, Input } from 'antd';
import { Line, Pie } from 'react-chartjs-2';
import moment from 'moment';

const { Option } = Select;

function Underwater() {
  // 状态声明
  const [selectedDate, setSelectedDate] = useState('2020-05-08');
  const [lineChartData, setLineChartData] = useState(null);
  const [pieChartData, setPieChartData] = useState(null);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedMetric, setSelectedMetric] = useState('');
  const [availableMetrics, setAvailableMetrics] = useState([]);

  // 常量配置
  const minDate = '2020-05-08';
  const maxDate = '2021-04-05';

  // 生成文件路径
  const generateFilePath = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `config/软件工程大作业数据/水质数据/${year}-${month}/${year}-${month}-${day}.json`;
  };

  // 提交处理
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      const filePath = generateFilePath(selectedDate);

      const response = await fetch('http://localhost:3001/visualize-water', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ file_path: filePath, target_column: selectedMetric })
      });

      if (!response.ok) throw new Error(await response.text());

      const data = await response.json();
      if (data.error) throw new Error(data.error);

      setLineChartData(data.line_chart_data);
      setPieChartData(data.pie_chart_data);
      setAvailableMetrics(data.available_metrics || []); // 新增：获取可用指标
    } catch (err) {
      setError(err.message);
      message.error(err.message); // 使用Ant Design的message提示错误
      setLineChartData(null);
      setPieChartData(null);
    } finally {
      setIsLoading(false);
    }
  };

  // 格式化日期显示
  const formatDate = (dateString) => {
    return moment(dateString).format('YYYY年MM月DD日');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center', color: '#1890ff', marginBottom: '30px' }}>
        水下环境监测可视化系统
      </h1>

      <Spin spinning={isLoading} tip="正在分析水质数据...">
        <form onSubmit={handleSubmit} style={{ marginBottom: '30px' }}>
          <Row gutter={16} justify="center">
            <Col xs={24} sm={12} md={8}>
              <Input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                min={minDate}
                max={maxDate}
                style={{ width: '100%' }}
                placeholder="选择日期"
              />
              <span style={{ display: 'block', marginTop: '8px', color: '#666' }}>
                选择日期: {formatDate(selectedDate)}
              </span>
            </Col>
            <Col xs={24} sm={12} md={8}>
              <Select
                value={selectedMetric}
                onChange={(value) => setSelectedMetric(value)}
                style={{ width: '100%' }}
                placeholder="选择监测指标"
              >
                <Option value="">显示全部指标</Option>
                {/* 动态生成指标选项，优先使用后端返回的指标 */}
                {availableMetrics.length > 0 ? (
                  availableMetrics.map((metric, index) => (
                    <Option key={index} value={metric}>{metric}</Option>
                  ))
                ) : (
                  // 备用静态选项
                  [
                    '断面名称', '水温(℃)', 'pH(无量纲)', '溶解氧(mg/L)',
                    '电导率(μS/cm)', '浊度(NTU)', '高锰酸盐指数(mg/L)',
                    '氨氮(mg/L)', '总磷(mg/L)', '总氮(mg/L)', '叶绿素α(mg/L)', '藻密度(cells/L)'
                  ].map((metric, index) => (
                    <Option key={index} value={metric}>{metric}</Option>
                  ))
                )}
              </Select>
            </Col>
            <Col xs={24} sm={24} md={8} style={{ textAlign: 'center', marginTop: '24px' }}>
              <Button
                type="primary"
                size="large"
                onClick={handleSubmit}
                loading={isLoading}
                style={{ width: '180px', height: '48px', fontSize: '16px' }}
              >
                {isLoading ? '数据生成中...' : '开始可视化分析'}
              </Button>
            </Col>
          </Row>
        </form>

        {error && (
          <Card
            style={{ marginBottom: '20px', borderColor: '#f5222d' }}
            title={<span style={{ color: '#f5222d' }}>⚠️ 错误提示</span>}
          >
            <p style={{ color: '#f5222d' }}>{error}</p>
          </Card>
        )}

        <Row gutter={16}>
          {lineChartData && (
            <Col xs={24} md={12}>
              <Card title="水质参数趋势分析" bordered={true}>
                <Line
                  data={{
                    labels: lineChartData.x,
                    datasets: [
                      {
                        label: selectedMetric || '全部指标',
                        data: lineChartData.y,
                        borderColor: '#1890ff',
                        backgroundColor: 'rgba(24, 144, 255, 0.1)',
                        borderWidth: 2,
                        tension: 0.3,
                        fill: true
                      }
                    ]
                  }}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        position: 'top',
                      },
                      tooltip: {
                        mode: 'index',
                        intersect: false,
                      }
                    },
                    scales: {
                      x: {
                        title: {
                          display: true,
                          text: '时间'
                        },
                        ticks: {
                          maxRotation: 45,
                          autoSkip: true,
                          maxTicksLimit: 8
                        }
                      },
                      y: {
                        title: {
                          display: true,
                          text: '数值'
                        }
                      }
                    }
                  }}
                />
              </Card>
            </Col>
          )}

          {pieChartData && (
            <Col xs={24} md={12}>
              <Card title="水质类别分布统计" bordered={true}>
                <Pie
                  data={{
                    labels: pieChartData.labels,
                    datasets: [
                      {
                        data: pieChartData.values,
                        backgroundColor: [
                          'rgba(255, 99, 132, 0.7)',
                          'rgba(54, 162, 235, 0.7)',
                          'rgba(255, 206, 86, 0.7)',
                          'rgba(75, 192, 192, 0.7)',
                          'rgba(153, 102, 255, 0.7)',
                          'rgba(255, 159, 64, 0.7)'
                        ],
                        borderColor: [
                          'rgb(255, 99, 132)',
                          'rgb(54, 162, 235)',
                          'rgb(255, 206, 86)',
                          'rgb(75, 192, 192)',
                          'rgb(153, 102, 255)',
                          'rgb(255, 159, 64)'
                        ],
                        borderWidth: 1
                      }
                    ]
                  }}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        position: 'right',
                      },
                      tooltip: {
                        callbacks: {
                          label: function(context) {
                            let label = context.label || '';
                            if (label) {
                              label += ': ';
                            }
                            if (context.parsed !== null) {
                              label += context.parsed + (context.dataIndex === 0 ? '%' : '');
                            }
                            return label;
                          }
                        }
                      }
                    }
                  }}
                />
              </Card>
            </Col>
          )}
        </Row>
      </Spin>
    </div>
  );
}

export default Underwater;
